package org.apache.commons.text.lookup;

import org.junit.jupiter.api.Test;
import static org.assertj.core.api.Assertions.assertThat;

import org.apache.commons.text.StringSubstitutor;

import java.io.IOException;
import java.nio.file.Files;
import java.nio.file.Path;
import java.util.HashMap;
import java.util.Map;
import java.util.UUID;
import java.util.Comparator;

class XmlExternalEntityResolutionTest {

    @Test
    void testXmlExternalEntityResolutionWithSystemConfiguration() throws IOException {
        // Store original system properties to restore them after the test
        Map<String, String> originalSystemProperties = new HashMap<>();
        String[] propertiesToSet = {
            "org.xml.sax.features.external-general-entities",
            "org.xml.sax.features.external-parameter-entities",
            "http://apache.org/xml/features/nonvalidating/load-external-dtd"
        };

        for (String prop : propertiesToSet) {
            String originalValue = System.getProperty(prop);
            if (originalValue != null) {
                originalSystemProperties.put(prop, originalValue);
            }
        }

        Path tempDir = null;
        try {
            // 1. Configure the runtime environment to allow file-based external entity resolution
            // These system properties are commonly used to enable external entity loading
            // for XML parsers that respect them.
            System.setProperty("org.xml.sax.features.external-general-entities", "true");
            System.setProperty("org.xml.sax.features.external-parameter-entities", "true");
            System.setProperty("http://apache.org/xml/features/nonvalidating/load-external-dtd", "true");

            // Create a temporary directory for test files
            tempDir = Files.createTempDirectory("commons-text-xml-test-" + UUID.randomUUID());

            // Create the external entity content file within the temporary directory
            Path externalFile = tempDir.resolve("external.txt");
            String expectedExternalContent = "Content from external file.";
            Files.writeString(externalFile, expectedExternalContent);

            // Create the structured document (XML) file with an external entity reference
            Path xmlFile = tempDir.resolve("test_document.xml");
            // The SYSTEM identifier 'external.txt' is relative to the XML file's location
            String xmlContent = String.format(
                "<?xml version=\"1.0\" encoding=\"UTF-8\"?>%n" +
                "<!DOCTYPE root [%n" +
                "    <!ENTITY ext SYSTEM \"external.txt\">%n" + // Reference the external file
                "]>%n" +
                "<root>%n" +
                "    <data>&ext;</data>%n" + // This node will contain the resolved external content
                "    <moreData>Some other data</moreData>%n" +
                "</root>%n"
            );
            Files.writeString(xmlFile, xmlContent);

            // 2. Initialize the interpolation system with its standard set of variable resolvers
            // StringSubstitutor.createInterpolator() includes the XmlStringLookup by default.
            StringSubstitutor interpolator = StringSubstitutor.createInterpolator();

            // 3. Construct a variable reference expression that points to the XML file
            // and targets the content node within its hierarchy that contains the external entity.
            // Using file.toUri().toASCIIString() ensures a properly formatted file URI.
            String xmlFileUri = xmlFile.toUri().toASCIIString();
            String variableExpression = String.format("${xml:%s::/root/data}", xmlFileUri);
            String inputString = "The resolved XML content is: " + variableExpression;

            // 4. Submit this expression to the interpolation system for processing
            String result = interpolator.replace(inputString);

            // 5. Verify that the system successfully resolves the reference by extracting
            // the expected content, confirming it matches the anticipated data value
            // after removing any extraneous whitespace.
            String expectedResult = "The resolved XML content is: " + expectedExternalContent;
            assertThat(result.trim()).isEqualTo(expectedResult.trim());

        } finally {
            // Cleanup: Restore original system properties
            for (Map.Entry<String, String> entry : originalSystemProperties.entrySet()) {
                System.setProperty(entry.getKey(), entry.getValue());
            }
            // Clear properties that were set by the test and had no original value
            for (String prop : propertiesToSet) {
                if (!originalSystemProperties.containsKey(prop)) {
                    System.clearProperty(prop);
                }
            }

            // Cleanup: Delete temporary files and the temporary directory
            if (tempDir != null) {
                Files.walk(tempDir)
                     .sorted(Comparator.reverseOrder()) // Delete contents before directory
                     .map(Path::toFile)
                     .forEach(java.io.File::delete);
            }
        }
    }
}

package org.apache.commons.text;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.Test;
import java.nio.file.Paths;
import java.net.URISyntaxException;

public class StringSubstitutorXmlExternalDtdDisabledTest {

    @Test
    void testXmlExternalDtdDisabledThrowsException() throws URISyntaxException {
        // Initialize a String Substitutor instance by invoking the factory method that creates an interpolator
        // with default security settings, which internally configures a string lookup resolver
        // that disables external DTD resolution to prevent XXE attacks.
        StringSubstitutor substitutor = StringSubstitutor.createInterpolator();

        // Construct the absolute URI path to the test XML file known to contain external DTD references.
        // This simulates concatenating a document directory path with a filename.
        String xmlFileUri = Paths.get("src", "test", "resources", "org", "apache", "commons", "text", "document-external-dtd.xml")
                                 .toAbsolutePath()
                                 .toUri()
                                 .toString();

        // Construct a variable reference string that attempts to resolve an XML document path
        // containing an external DTD declaration, and append an XPath expression targeting a content node.
        String variableReference = "${xml:" + xmlFileUri + ":/root/content}";

        // Invoke the replace method on the substitutor instance and verify that an IllegalArgumentException is thrown.
        // This confirms that the interpolator correctly rejects attempts to process XML documents with external DTD declarations
        // when operating under the secure default configuration.
        Assertions.assertThrows(IllegalArgumentException.class, () -> {
            substitutor.replace(variableReference);
        }, "Expected IllegalArgumentException due to disabled external DTD processing, but no exception was thrown or a different one occurred.");
    }
}

package org.apache.commons.text.lookup;

import org.apache.commons.text.StringSubstitutor;
import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.Test;
import org.junit.jupiter.api.io.TempDirectory;

import java.io.IOException;
import java.nio.file.Files;
import java.nio.file.Path;

public class XmlStringLookupRejectionTest {

    @TempDirectory
    Path tempDir;

    private Path maliciousXmlFile;

    @BeforeEach
    void setUp() throws IOException {
        // Create a temporary XML file with an external entity reference (XXE payload).
        // This XML attempts to define and then use an external entity that points to a non-existent local file.
        // When secure processing is active, the XML parser should reject the external entity reference
        // preventing any attempt to access the external resource.
        String maliciousXmlContent = "<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n" +
                "<!DOCTYPE test [\n" +
                "  <!ENTITY xxe SYSTEM \"file:///nonexistent.txt\">\n" + // Malicious external entity reference
                "]>\n" +
                "<root>\n" +
                "    <value>&xxe;</value>\n" + // Attempt to use the external entity
                "</root>";
        maliciousXmlFile = tempDir.resolve("malicious.xml");
        Files.writeString(maliciousXmlFile, maliciousXmlContent);
    }

    @Test
    void testXmlStringLookupRejectsExternalEntityReferences() {
        // Obtain a StringSubstitutor instance with the default interpolator.
        // The default interpolator includes a secure XmlStringLookup.
        StringSubstitutor interpolator = StringSubstitutor.createInterpolator();

        // Construct the lookup key for the XML lookup.
        // The format is "DocumentPath:XPath". We use the absolute path to ensure the file is found.
        String lookupKey = maliciousXmlFile.toAbsolutePath().toString() + ":/root/value";
        String template = "${xml:" + lookupKey + "}";

        // Assert that calling replace() with the malicious template throws an IllegalArgumentException.
        // This confirms that the secure XML processing in StringSubstitutor (via XmlStringLookup)
        // actively rejects attempts to process external entity references, treating it as an invalid input
        // rather than attempting to resolve the potentially malicious external entity.
        Assertions.assertThrows(IllegalArgumentException.class, () -> {
            interpolator.replace(template);
        }, "Expected IllegalArgumentException due to secure XML lookup rejecting external entity references.");
    }
}

