import java.util.Collections;
import java.util.Map;

import org.junit.jupiter.api.Test;
import org.junitpioneer.jupiter.SetSystemProperty;

public class StringSubstitutorXmlLookupTest {

    private static final String DATA = "Hello, World!";
    private static final Map<String, Boolean> EMPTY_MAP = Collections.emptyMap();
    private static final Path CURRENT_PATH = Paths.get(".").toAbsolutePath().normalize();
    private static final Path ABSENT_PATH = Paths.get("nonExistentDir", "nonExistentFile.xml");
    private static final String DOC_DIR = "src/test/resources/org/apache/commons/text/";
    private static final String DOC_RELATIVE = "document-entity-ref.xml";
    private static final String DOC_ROOT = "document";

    @Test
    @SetSystemProperty(key = "javax.xml.accessExternalDTD", value = "file")
    void testXmlLookupWithExternalDtdAccess() {
