class XmlStringLookupExternalEntityTest {

    // The project's temporary directory provided in the prompt.
    private static final Path PROJECT_TEMP_DIR = Paths.get("/home/bmcginn/.gemini/tmp/76b335236c11971d9490234d5b48bff6d0e6afd22b03bc801c7c50691b8e65cb");

    private Path tempDtdFile;
    private Path tempXmlFile;
    private Map<String, String> originalSystemProperties = new HashMap<>();

    @BeforeEach
    void setUp() throws IOException {
        // Save original system properties to restore them later
        originalSystemProperties.put(XMLConstants.FEATURE_SECURE_PROCESSING, System.getProperty(XMLConstants.FEATURE_SECURE_PROCESSING));
        originalSystemProperties.put(XMLConstants.ACCESS_EXTERNAL_DTD, System.getProperty(XMLConstants.ACCESS_EXTERNAL_DTD));
        originalSystemProperties.put("http://xml.org/sax/features/external-general-entities", System.getProperty("http://xml.org/sax/features/external-general-entities"));
        originalSystemProperties.put("http://xml.org/sax/features/external-parameter-entities", System.getProperty("http://xml.org/sax/features/external-parameter-entities"));

        // Configure system to permit file-based external DTD access
        // Disabling secure processing and enabling specific features for external entities
        System.setProperty(XMLConstants.FEATURE_SECURE_PROCESSING, "false");
        System.setProperty(XMLConstants.ACCESS_EXTERNAL_DTD, "file"); // Allow DTDs only from 'file' protocol
        System.setProperty("http://xml.org/sax/features/external-general-entities", "true");
        System.setProperty("http://xml.org/sax/features/external-parameter-entities", "true");

        // Create temporary DTD file defining an external entity
        final String dtdContent = "<!ENTITY ext \"External Content Value\">";
        tempDtdFile = Files.createTempFile(PROJECT_TEMP_DIR, "temp", ".dtd");
        Files.write(tempDtdFile, dtdContent.getBytes(StandardCharsets.UTF_8));

        // Create temporary XML file referencing the DTD and using the external entity
        final String xmlContent = String.format(
            "<!DOCTYPE foo SYSTEM \"%s\">\n" +
            "<root>\n" +
            "    <value>&ext;</value>\n" +
            "</root>",
            tempDtdFile.toUri().toString() // Use URI for SYSTEM identifier
        );
        tempXmlFile = Files.createTempFile(PROJECT_TEMP_DIR, "test", ".xml");
        Files.write(tempXmlFile, xmlContent.getBytes(StandardCharsets.UTF_8));
    }

    @AfterEach
    void tearDown() throws IOException {
        // Clean up temporary files
        Files.deleteIfExists(tempXmlFile);
        Files.deleteIfExists(tempDtdFile);

        // Restore original system properties to avoid side effects on other tests
        for (final Map.Entry<String, String> entry : originalSystemProperties.entrySet()) {
            if (entry.getValue() != null) {
                System.setProperty(entry.getKey(), entry.getValue());
            } else {
                System.clearProperty(entry.getKey());
            }
        }
    }

    @Test
    void testExternalEntityResolutionWithEnabledFileDtdAccess() {
        // Initialize an interpolation service capable of processing variable substitutions
        // StringSubstitutor.createInterpolator() includes the XmlStringLookup by default
        final StringSubstitutor substitutor = StringSubstitutor.createInterpolator();

        // Prepare a substitution request that references content from an external document
        // The key format for XmlStringLookup is typically "xml:file:/path/to/xml.xml:xpath"
        // targeting a path within the document structure that should resolve to a predefined text value
        final String lookupKey = "xml:" + tempXmlFile.toUri().toString() + ":/root/value/text()";
        final String substitutionString = "${" + lookupKey + "}";

        // Execute the interpolation operation
        final String resolvedValue = substitutor.replace(substitutionString);

        // Verify the system successfully retrieves and processes the external entity,
        // confirming the output matches the expected content after removing any extraneous whitespace.
        assertThat(resolvedValue).isEqualTo("External Content Value");
    }
}

