package org.apache.commons.text.translate;

import org.junit.jupiter.api.Test;
import static org.junit.jupiter.api.Assertions.assertEquals;
import static org.junit.jupiter.api.Assertions.assertTrue;

import java.io.IOException;
import java.io.Writer;

public class AggregateTranslatorCompositionTest {

    private static int translationCounter;

    private static class TestTranslator extends CharSequenceTranslator {
        @Override
        public int translate(final CharSequence input, final int index, final Writer out) throws IOException {
            translationCounter++;
            return 0; // Return 0 as no characters were consumed or translated
        }
    }

    @Test
    void testAggregateTranslatorComposition() throws IOException {
        translationCounter = 0; // Reset counter for each test execution

        final TestTranslator translator1 = new TestTranslator();
        final TestTranslator translator2 = new TestTranslator();
        final TestTranslator translator3 = new TestTranslator();

        final CharSequenceTranslator aggregatedTranslator = translator1.with(translator2, translator3);

        assertTrue(aggregatedTranslator instanceof AggregateTranslator,
            "The aggregated translator should be an instance of AggregateTranslator.");

        // Invoke translate to trigger the internal translation logic across all composed translators
        // Use empty string, zero index, and null writer as per requirements.
        aggregatedTranslator.translate("", 0, null);

        assertEquals(3, translationCounter,
            "All three composed translators should have been invoked, hence the counter should be 3.");
    }
}

package org.apache.commons.text.translate;

import static org.junit.jupiter.api.Assertions.assertEquals;
import static org.junit.jupiter.api.Assertions.assertNotNull;
import static org.junit.jupiter.api.Assertions.assertTrue;

import java.io.IOException;
import java.io.StringWriter;
import java.io.Writer;
import java.util.concurrent.atomic.AtomicInteger;

import org.junit.jupiter.api.Test;

public class AggregateTranslatorInvocationTest {

    /**
     * A simple CharSequenceTranslator that counts its invocations.
     */
    private static class CountingTranslator extends CharSequenceTranslator {
        private final AtomicInteger invocationCount = new AtomicInteger(0);

        @Override
        public int translate(final CharSequence input, final int index, final Writer out) throws IOException {
            invocationCount.incrementAndGet();
            // This translator doesn't actually translate anything, it just counts invocations.
            // Returning 0 means no characters were consumed or translated by this specific rule.
            return 0;
        }

        public int getInvocationCount() {
            return invocationCount.get();
        }
    }

    @Test
    public void testMultipleTranslatorsInvokedWithEmptyInput() throws IOException {
        // 1. Establish three separate text transformation rules
        final CountingTranslator translator1 = new CountingTranslator();
        final CountingTranslator translator2 = new CountingTranslator();
        final CountingTranslator translator3 = new CountingTranslator();

        // 2. Combine them into a single unified transformation process
        final AggregateTranslator aggregateTranslator = new AggregateTranslator(translator1, translator2, translator3);

        // Confirm that the resulting combined transformation process uses the appropriate aggregation strategy
        assertNotNull(aggregateTranslator, "AggregateTranslator should not be null");
        assertTrue(aggregateTranslator instanceof AggregateTranslator, "The combined translator should be an instance of AggregateTranslator");

        // 3. Trigger it by attempting to transform an empty text input
        final StringWriter writer = new StringWriter();
        aggregateTranslator.translate("", writer);

        // 4. Verify that all three individual transformation rules were invoked
        assertEquals(1, translator1.getInvocationCount(), "Translator 1 should have been invoked once");
        assertEquals(1, translator2.getInvocationCount(), "Translator 2 should have been invoked once");
        assertEquals(1, translator3.getInvocationCount(), "Translator 3 should have been invoked once");
    }
}

package org.apache.commons.text.translate;

import static org.junit.jupiter.api.Assertions.assertEquals;
import static org.junit.jupiter.api.Assertions.assertInstanceOf;

import java.io.IOException;
import java.io.Writer;
import org.junit.jupiter.api.Test;

public class AggregateTranslatorInvocationTest {

    private int translateInvocationCounter = 0;

    @Test
    public void testAggregateTranslatorInvocationCounting() throws IOException {
        final CharSequenceTranslator charSequenceTranslatorOne = new CharSequenceTranslator() {
            @Override
            public int translate(final CharSequence input, final int index, final Writer out) throws IOException {
                translateInvocationCounter++;
                return 0;
            }
        };

        final CharSequenceTranslator charSequenceTranslatorTwo = new CharSequenceTranslator() {
            @Override
            public int translate(final CharSequence input, final int index, final Writer out) throws IOException {
                translateInvocationCounter++;
                return 0;
            }
        };

        final CharSequenceTranslator charSequenceTranslatorThree = new CharSequenceTranslator() {
            @Override
            public int translate(final CharSequence input, final int index, final Writer out) throws IOException {
                translateInvocationCounter++;
                return 0;
            }
        };

        final CharSequenceTranslator aggregatedTranslator = charSequenceTranslatorOne.with(
            charSequenceTranslatorTwo, charSequenceTranslatorThree);

        aggregatedTranslator.translate("", 0, null);

        assertInstanceOf(AggregateTranslator.class, aggregatedTranslator);
        assertEquals(3, translateInvocationCounter);
    }
}

