package org.apache.commons.text.similarity;

import static org.junit.jupiter.api.Assertions.assertThrows;

import org.junit.jupiter.api.Test;

class LevenshteinDistanceInputValidationTest {

    @Test
    void testNullInputRejection() {
        // Configure the comparison engine to operate without distance limitations.
        // The default constructor of LevenshteinDistance sets the threshold to -1,
        // which signifies no distance limit according to its implementation.
        final LevenshteinDistance distance = new LevenshteinDistance();

        // Scenario 1: Left comparison text is missing while the right side contains valid content
        assertThrows(IllegalArgumentException.class, () -> distance.apply(null, "right side content"),
                "Expected IllegalArgumentException when the left comparison text is null.");

        // Scenario 2: Right comparison text is missing while the left side is provided
        assertThrows(IllegalArgumentException.class, () -> distance.apply("left side content", null),
                "Expected IllegalArgumentException when the right comparison text is null.");

        // Scenario 3: Left input wrapper is missing despite having valid right-side content
        // Interpreting "left input wrapper missing" as the CharSequence provided for the left input being null.
        assertThrows(IllegalArgumentException.class, () -> distance.apply(null, "another right side content"),
                "Expected IllegalArgumentException when the left input (interpreted as wrapper) is null.");

        // Scenario 4: Right input wrapper is missing while the left side is properly configured
        // Interpreting "right input wrapper missing" as the CharSequence provided for the right input being null.
        assertThrows(IllegalArgumentException.class, () -> distance.apply("another left side content", null),
                "Expected IllegalArgumentException when the right input (interpreted as wrapper) is null.");
    }
}

