package org.apache.commons.text.similarity;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.Test;

public class DamerauLevenshteinDistanceNullInputTest {

    private DamerauLevenshteinDistance distanceCalculator;

    @BeforeEach
    void setUp() {
        // Initialize with default constructor that operates without a threshold limit.
        distanceCalculator = new DamerauLevenshteinDistance();
    }

    @Test
    void testNullInputRejection() {
        final CharSequence validCharSequence = "test";
        // Create a valid SimilarityInput object by wrapping a CharSequence.
        final SimilarityInput<Character> validSimilarityInput = SimilarityInput.input("test");

        // Verify that invoking the apply method with a null left argument and a valid string right argument
        // triggers an illegal argument exception.
        Assertions.assertThrows(IllegalArgumentException.class, () ->
            distanceCalculator.apply(null, validCharSequence),
            "Expected IllegalArgumentException for null left CharSequence in apply(CharSequence, CharSequence)"
        );

        // Confirm the same exception occurs when the left argument is valid and the right argument is null.
        Assertions.assertThrows(IllegalArgumentException.class, () ->
            distanceCalculator.apply(validCharSequence, null),
            "Expected IllegalArgumentException for null right CharSequence in apply(CharSequence, CharSequence)"
        );

        // Extend this validation to the overloaded apply method that accepts SimilarityInput objects
        // by verifying that passing null as the left argument while providing the wrapped input as the right argument
        // throws an illegal argument exception.
        Assertions.assertThrows(IllegalArgumentException.class, () ->
            distanceCalculator.apply((SimilarityInput<Character>) null, validSimilarityInput),
            "Expected IllegalArgumentException for null left SimilarityInput in apply(SimilarityInput, SimilarityInput)"
        );

        // Similarly confirm that passing the wrapped input as the left argument with null as the right argument
        // also triggers the same exception type.
        Assertions.assertThrows(IllegalArgumentException.class, () ->
            distanceCalculator.apply(validSimilarityInput, (SimilarityInput<Character>) null),
            "Expected IllegalArgumentException for null right SimilarityInput in apply(SimilarityInput, SimilarityInput)"
        );
    }
}

