package org.apache.commons.text;

import org.apache.commons.text.lookup.StringLookupFactory;
import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.Test;
import org.junit.jupiter.api.io.TempDir;

import java.io.IOException;
import java.nio.file.Files;
import java.nio.file.Path;

/**
 * Tests {@link StringSubstitutor} with XML lookup capable of processing external DTDs.
 */
public class XmlStringSubstitutorExternalDtdTest {

    /**
     * Temporary directory for test files managed by JUnit.
     */
    @TempDir
    Path tempDir;

    /**
     * Tests XML variable interpolation with external DTD processing enabled by configuring
     * the system property {@code javax.xml.accessExternalDTD} to allow file protocol access.
     * It verifies that the {@link StringSubstitutor} successfully resolves an XML variable
     * reference, parses the external DTD, and extracts the targeted node content.
     *
     * @throws IOException if an I/O error occurs during file operations.
     */
    @Test
    void testXmlSubstitutorWithExternalDtdAndFileProtocol() throws IOException {
        // Prepare paths for the XML document and its external DTD file
        final Path dtdFile = tempDir.resolve("document-external-dtd.dtd");
        final Path xmlFile = tempDir.resolve("document.xml");

        // Define the content for the external DTD, which includes an entity definition
        final String externalEntityContent = "External DTD Content";
        final String dtdContent = "<!ENTITY externalEntity \"" + externalEntityContent + "\">";

        // Define the content for the XML document, referencing the external DTD and the entity
        final String xmlContent =
            "<!DOCTYPE root SYSTEM \"" + dtdFile.toUri().toString() + "\">\n" +
            "<root>\n" +
            "    <element>&externalEntity;</element>\n" +
            "</root>";

        // Write the defined content to the temporary DTD and XML files
        Files.writeString(dtdFile, dtdContent);
        Files.writeString(xmlFile, xmlContent);

        // Configure the system property to allow file protocol access for external DTDs.
        // This is crucial for security managers to permit loading the DTD from a local file path.
        final String originalAccessExternalDTD = System.setProperty("javax.xml.accessExternalDTD", "file");
        try {
            // Initialize StringSubstitutor with the interpolator string lookup, which includes
            // capabilities like XmlStringLookup for XML document parsing.
            final StringSubstitutor substitutor = StringSubstitutor.createInterpolator();

            // Construct the interpolation expression string.
            // It uses the 'xml' lookup prefix, followed by the URI of the XML file,
            // and an XPath expression to target the text content of the 'element' tag.
            // The XmlStringLookup implicitly handles the DTD resolution during parsing.
            final String interpolationExpression = "${xml:" + xmlFile.toUri().toString() + ":/root/element/text()}";

            // Invoke the replace method on the substitutor to process the expression
            final String result = substitutor.replace(interpolationExpression);

            // Assert that the trimmed result matches the expected content from the external entity,
            // confirming successful XML variable resolution, DTD parsing, and content extraction.
            Assertions.assertEquals(externalEntityContent, result.trim());

        } finally {
            // Ensure the system property is restored to its original value or cleared
            // to prevent side effects on other tests or applications.
            if (originalAccessExternalDTD != null) {
                System.setProperty("javax.xml.accessExternalDTD", originalAccessExternalDTD);
            } else {
                System.clearProperty("javax.xml.accessExternalDTD");
            }
        }
    }
}

