class XmlStringLookupSecurityTest {

    @Test
    void testLookupWithExternalEntityReferenceThrowsIllegalArgumentException() {
        // Arrange
        final StringSubstitutor substitutor = StringSubstitutor.createInterpolator();

        // Craft a malicious XML payload attempting to declare an external entity
        // The XmlStringLookup is configured to prevent external DTDs and entities,
        // so this should trigger an IllegalArgumentException.
        final String maliciousXmlDocument = "<!DOCTYPE foo [<!ENTITY xxe SYSTEM "file:///nonexistent">]><foo>&xxe;</foo>";
        final String lookupKey = maliciousXmlDocument + "::/foo";
        final String inputString = "The value is ${xml:" + lookupKey + "}.";

        // Act & Assert
        // Expect an IllegalArgumentException because XmlStringLookup explicitly disables external entities
        // and DTDs, and wraps any SAXException or ParserConfigurationException in an IllegalArgumentException.
        Assertions.assertThrows(IllegalArgumentException.class, () -> substitutor.replace(inputString),
            "Expected IllegalArgumentException for external entity reference attempt, but none was thrown.");
    }
}

