package org.apache.commons.text;

import org.junit.jupiter.api.Test;
import static org.assertj.core.api.Assertions.assertThat;
import static org.assertj.core.api.Assertions.assertThatThrownBy;

class TextStringBuilderInsertionTest {

    @Test
    void testInsertionsWithBoundaryProtection() {
        // Begin with an empty text composition instance and confirm its initial empty state
        final TextStringBuilder builder = new TextStringBuilder();
        assertThat(builder.isEmpty()).isTrue();
        assertThat(builder.length()).isEqualTo(0);
        assertThat(builder.toString()).isEqualTo("");

        // Insert a text segment at the start position and verify the content matches the expected text
        builder.insert(0, "Hello");
        assertThat(builder.toString()).isEqualTo("Hello");
        assertThat(builder.length()).isEqualTo(5);

        // Attempt to insert content at positions before the valid range
        assertThatThrownBy(() -> builder.insert(-1, "invalid"))
                .isInstanceOf(IndexOutOfBoundsException.class)
                .hasMessageContaining("Index must be valid and within range [0, 5). Was: -1");
        assertThatThrownBy(() -> builder.insert(-5, 'c'))
                .isInstanceOf(IndexOutOfBoundsException.class)
                .hasMessageContaining("Index must be valid and within range [0, 5). Was: -5");

        // Attempt to insert content at positions beyond the current end boundary
        assertThatThrownBy(() -> builder.insert(6, "invalid"))
                .isInstanceOf(IndexOutOfBoundsException.class)
                .hasMessageContaining("Index must be valid and within range [0, 5). Was: 6");
        assertThatThrownBy(() -> builder.insert(10, 'c'))
                .isInstanceOf(IndexOutOfBoundsException.class)
                .hasMessageContaining("Index must be valid and within range [0, 5). Was: 10");

        // Insert a boolean value at the current end position and confirm the system appends the text representation of that value
        builder.insert(builder.length(), true);
        assertThat(builder.toString()).isEqualTo("Hellotrue");
        assertThat(builder.length()).isEqualTo(9);

        // Continue by attempting insertions at positions outside the updated valid range to verify boundary enforcement remains intact
        assertThatThrownBy(() -> builder.insert(10, "invalid"))
                .isInstanceOf(IndexOutOfBoundsException.class)
                .hasMessageContaining("Index must be valid and within range [0, 9). Was: 10");
        assertThatThrownBy(() -> builder.insert(-1, "invalid"))
                .isInstanceOf(IndexOutOfBoundsException.class)
                .hasMessageContaining("Index must be valid and within range [0, 9). Was: -1");

        // Insert a single character at the end and validate the updated content
        builder.insert(builder.length(), '!');
        assertThat(builder.toString()).isEqualTo("Hellotrue!");
        assertThat(builder.length()).isEqualTo(10);

        // Proceed with inserting a sequence of characters at the end position and confirm they appear in the output
        builder.insert(builder.length(), " World");
        assertThat(builder.toString()).isEqualTo("Hellotrue! World");
        assertThat(builder.length()).isEqualTo(16);

        // Verifying that attempts to insert at positions beyond the new boundary are properly rejected
        assertThatThrownBy(() -> builder.insert(17, "invalid"))
                .isInstanceOf(IndexOutOfBoundsException.class)
                .hasMessageContaining("Index must be valid and within range [0, 16). Was: 17");

        // Insert a portion of a character sequence by specifying a subset range
        builder.insert(builder.length(), "FULLTEXT", 0, 4); // Inserts "FULL"
        assertThat(builder.toString()).isEqualTo("Hellotrue! WorldFULL");
        assertThat(builder.length()).isEqualTo(20);

        // Ensure invalid positions continue to be blocked for portion insertion
        assertThatThrownBy(() -> builder.insert(builder.length(), "FULLTEXT", -1, 4))
                .isInstanceOf(IndexOutOfBoundsException.class)
                .hasMessageContaining("The offset must be greater than zero and less than the length of the CharSequence. Was: -1");
        assertThatThrownBy(() -> builder.insert(builder.length(), "FULLTEXT", 0, 9))
                .isInstanceOf(IndexOutOfBoundsException.class)
                .hasMessageContaining("The offset plus the length must not exceed the length of the CharSequence. Was: 9");
        assertThatThrownBy(() -> builder.insert(builder.length() + 1, "FULLTEXT", 0, 4))
                .isInstanceOf(IndexOutOfBoundsException.class)
                .hasMessageContaining("Index must be valid and within range [0, 20). Was: 21");

        // Insert numeric values of various types at the end position
        builder.insert(builder.length(), 123); // int
        assertThat(builder.toString()).isEqualTo("Hellotrue! WorldFULL123");
        assertThat(builder.length()).isEqualTo(23);

        final int currentLengthAfterInt = builder.length();
        assertThatThrownBy(() -> builder.insert(currentLengthAfterInt + 1, 999))
                .isInstanceOf(IndexOutOfBoundsException.class)
                .hasMessageContaining("Index must be valid and within range [0, 23). Was: 24");

        builder.insert(builder.length(), 456L); // long
        assertThat(builder.toString()).isEqualTo("Hellotrue! WorldFULL123456");
        assertThat(builder.length()).isEqualTo(26);

        final int currentLengthAfterLong = builder.length();
        assertThatThrownBy(() -> builder.insert(currentLengthAfterLong + 1, 999L))
                .isInstanceOf(IndexOutOfBoundsException.class)
                .hasMessageContaining("Index must be valid and within range [0, 26). Was: 27");

        builder.insert(builder.length(), 7.89f); // float
        assertThat(builder.toString()).isEqualTo("Hellotrue! WorldFULL1234567.89");
        assertThat(builder.length()).isEqualTo(30);

        final int currentLengthAfterFloat = builder.length();
        assertThatThrownBy(() -> builder.insert(currentLengthAfterFloat + 1, 999.9f))
                .isInstanceOf(IndexOutOfBoundsException.class)
                .hasMessageContaining("Index must be valid and within range [0, 30). Was: 31");

        builder.insert(builder.length(), 10.11); // double
        assertThat(builder.toString()).isEqualTo("Hellotrue! WorldFULL1234567.8910.11");
        assertThat(builder.length()).isEqualTo(35);

        final int currentLengthAfterDouble = builder.length();
        assertThatThrownBy(() -> builder.insert(currentLengthAfterDouble + 1, 999.99))
                .isInstanceOf(IndexOutOfBoundsException.class)
                .hasMessageContaining("Index must be valid and within range [0, 35). Was: 36");

        // Finally, insert an object reference at the end position
        Object obj = new Object() {
            @Override
            public String toString() {
                return "OBJ";
            }
        };
        builder.insert(builder.length(), obj);
        assertThat(builder.toString()).isEqualTo("Hellotrue! WorldFULL1234567.8910.11OBJ");
        assertThat(builder.length()).isEqualTo(38);

        // Confirming to reject out-of-bounds insertion attempts throughout the entire sequence
        final int finalLength = builder.length();
        assertThatThrownBy(() -> builder.insert(finalLength + 1, "final invalid"))
                .isInstanceOf(IndexOutOfBoundsException.class)
                .hasMessageContaining("Index must be valid and within range [0, 38). Was: 39");
    }
}

package org.apache.commons.text;

import static org.junit.jupiter.api.Assertions.assertEquals;
import static org.junit.jupiter.api.Assertions.assertThrows;

import org.junit.jupiter.api.Test;

public class TestTextStringBuilderInsertOperations {

    @Test
    public void testInsertOperations() {
        // Instantiate a new TextStringBuilder and verify initial state
        TextStringBuilder builder = new TextStringBuilder();
        assertEquals("", builder.toString(), "Initial builder should be empty");

        // Insert a string literal at position zero
        builder.insert(0, "World");
        assertEquals("World", builder.toString(), "Builder content after inserting 'World'");

        // Insert another string at position zero
        builder.insert(0, "Hello ");
        assertEquals("Hello World", builder.toString(), "Builder content after inserting 'Hello ' at 0");

        // Assert that attempts to insert at negative indices trigger index out of bounds errors
        assertThrows(StringIndexOutOfBoundsException.class, () -> builder.insert(-1, "FAIL"),
                "Inserting at negative index should throw StringIndexOutOfBoundsException");

        // Assert that attempts to insert at positions beyond the current length trigger index out of bounds errors
        final int currentLength = builder.length();
        assertThrows(StringIndexOutOfBoundsException.class, () -> builder.insert(currentLength + 1, "FAIL"),
                "Inserting beyond current length should throw StringIndexOutOfBoundsException");
        // Also test at exactly current length which should be valid
        builder.insert(currentLength, "!");
        assertEquals("Hello World!", builder.toString(), "Inserting at current length should append");


        // Insert a boolean value at the current end position
        builder = new TextStringBuilder(); // Reset builder for clarity
        builder.insert(0, true);
        assertEquals("true", builder.toString(), "Builder content after inserting boolean 'true'");
        builder.insert(builder.length(), false);
        assertEquals("truefalse", builder.toString(), "Builder content after inserting boolean 'false'");

        // Verify that insertions at invalid positions beyond the new length or negative offsets produce boundary exceptions for boolean
        assertThrows(StringIndexOutOfBoundsException.class, () -> builder.insert(-1, true),
                "Inserting boolean at negative index should throw StringIndexOutOfBoundsException");
        assertThrows(StringIndexOutOfBoundsException.class, () -> builder.insert(builder.length() + 1, false),
                "Inserting boolean beyond current length should throw StringIndexOutOfBoundsException");


        // Insert a character at the terminal position
        builder = new TextStringBuilder(); // Reset builder
        builder.insert(0, 'A');
        assertEquals("A", builder.toString(), "Builder content after inserting char 'A'");
        builder.insert(builder.length(), 'B');
        assertEquals("AB", builder.toString(), "Builder content after inserting char 'B'");

        // Ensure invalid index insertions fail appropriately for char
        assertThrows(StringIndexOutOfBoundsException.class, () -> builder.insert(-1, 'C'),
                "Inserting char at negative index should throw StringIndexOutOfBoundsException");
        assertThrows(StringIndexOutOfBoundsException.class, () -> builder.insert(builder.length() + 1, 'D'),
                "Inserting char beyond current length should throw StringIndexOutOfBoundsException");


        // Insert a character array at the end position
        builder = new TextStringBuilder(); // Reset builder
        char[] charArray1 = {'x', 'y', 'z'};
        builder.insert(0, charArray1);
        assertEquals("xyz", builder.toString(), "Builder content after inserting char array 'xyz'");
        char[] charArray2 = {'1', '2', '3'};
        builder.insert(builder.length(), charArray2);
        assertEquals("xyz123", builder.toString(), "Builder content after inserting char array '123'");

        // Verify that array insertions at positions exceeding the current size or negative indices raise exceptions
        assertThrows(StringIndexOutOfBoundsException.class, () -> builder.insert(-1, charArray1),
                "Inserting char array at negative index should throw StringIndexOutOfBoundsException");
        assertThrows(StringIndexOutOfBoundsException.class, () -> builder.insert(builder.length() + 1, charArray1),
                "Inserting char array beyond current length should throw StringIndexOutOfBoundsException");


        // Insert a subset of a character array using offset and length parameters at the terminal position
        builder = new TextStringBuilder(); // Reset builder
        char[] charArrayFull = {'a', 'b', 'c', 'd', 'e'};
        builder.insert(0, charArrayFull, 1, 3); // insert "bcd"
        assertEquals("bcd", builder.toString(), "Builder content after inserting subset of char array 'bcd'");

        // Verify that invalid positioning for partial array insertions triggers appropriate errors
        assertThrows(StringIndexOutOfBoundsException.class, () -> builder.insert(0, charArrayFull, -1, 2),
                "Inserting partial char array with negative offset should throw StringIndexOutOfBoundsException");
        assertThrows(StringIndexOutOfBoundsException.class, () -> builder.insert(0, charArrayFull, 0, -1),
                "Inserting partial char array with negative length should throw StringIndexOutOfBoundsException");
        assertThrows(StringIndexOutOfBoundsException.class, () -> builder.insert(0, charArrayFull, 0, 10),
                "Inserting partial char array with length exceeding array bounds should throw StringIndexOutOfBoundsException");
        assertThrows(StringIndexOutOfBoundsException.class, () -> builder.insert(-1, charArrayFull, 0, 1),
                "Inserting partial char array at negative builder index should throw StringIndexOutOfBoundsException");
        assertThrows(StringIndexOutOfBoundsException.class, () -> builder.insert(builder.length() + 1, charArrayFull, 0, 1),
                "Inserting partial char array beyond builder length should throw StringIndexOutOfBoundsException");


        // Continue by inserting a double value at the end
        builder = new TextStringBuilder(); // Reset builder
        builder.insert(0, 123.45);
        assertEquals("123.45", builder.toString(), "Builder content after inserting double '123.45'");

        // Confirm boundary violations for double insertions are detected
        assertThrows(StringIndexOutOfBoundsException.class, () -> builder.insert(-1, 67.89),
                "Inserting double at negative index should throw StringIndexOutOfBoundsException");
        assertThrows(StringIndexOutOfBoundsException.class, () -> builder.insert(builder.length() + 1, 67.89),
                "Inserting double beyond current length should throw StringIndexOutOfBoundsException");


        // Then insert a float value at the terminal position
        builder = new TextStringBuilder(); // Reset builder
        builder.insert(0, 10.5f);
        assertEquals("10.5", builder.toString(), "Builder content after inserting float '10.5f'");

        // Verify its conversion to string format is correctly appended with corresponding boundary checks
        assertThrows(StringIndexOutOfBoundsException.class, () -> builder.insert(-1, 20.3f),
                "Inserting float at negative index should throw StringIndexOutOfBoundsException");
        assertThrows(StringIndexOutOfBoundsException.class, () -> builder.insert(builder.length() + 1, 20.3f),
                "Inserting float beyond current length should throw StringIndexOutOfBoundsException");


        // Proceed to insert an integer at the end position
        builder = new TextStringBuilder(); // Reset builder
        builder.insert(0, 99);
        assertEquals("99", builder.toString(), "Builder content after inserting int '99'");

        // Confirm the numeric string representation is incorporated while ensuring invalid index attempts fail
        assertThrows(StringIndexOutOfBoundsException.class, () -> builder.insert(-1, 100),
                "Inserting int at negative index should throw StringIndexOutOfBoundsException");
        assertThrows(StringIndexOutOfBoundsException.class, () -> builder.insert(builder.length() + 1, 100),
                "Inserting int beyond current length should throw StringIndexOutOfBoundsException");


        // Insert a long value at the terminal position
        builder = new TextStringBuilder(); // Reset builder
        builder.insert(0, 123456789L);
        assertEquals("123456789", builder.toString(), "Builder content after inserting long '123456789L'");

        // Validate its string conversion appears correctly with appropriate exception handling for boundary violations
        assertThrows(StringIndexOutOfBoundsException.class, () -> builder.insert(-1, 98765L),
                "Inserting long at negative index should throw StringIndexOutOfBoundsException");
        assertThrows(StringIndexOutOfBoundsException.class, () -> builder.insert(builder.length() + 1, 98765L),
                "Inserting long beyond current length should throw StringIndexOutOfBoundsException");


        // Finally insert an object reference at the end position
        builder = new TextStringBuilder(); // Reset builder
        Object testObject = new Object() {
            @Override
            public String toString() {
                return "TEST_OBJ";
            }
        };
        builder.insert(0, testObject);
        assertEquals("TEST_OBJ", builder.toString(), "Builder content after inserting object 'TEST_OBJ'");

        // Verify the object's string representation is appended to the builder while confirming that insertions beyond valid indices or at negative positions produce index out of bounds errors
        assertThrows(StringIndexOutOfBoundsException.class, () -> builder.insert(-1, new Object()),
                "Inserting object at negative index should throw StringIndexOutOfBoundsException");
        assertThrows(StringIndexOutOfBoundsException.class, () -> builder.insert(builder.length() + 1, new Object()),
                "Inserting object beyond current length should throw StringIndexOutOfBoundsException");

        // Test inserting null string with default nullText behavior
        builder = new TextStringBuilder();
        builder.setNullText("NULL");
        builder.insert(0, (String) null);
        assertEquals("NULL", builder.toString(), "Inserting null string should use nullText");

        // Test inserting null char array with default nullText behavior
        builder = new TextStringBuilder();
        builder.setNullText("NULL");
        builder.insert(0, (char[]) null);
        assertEquals("NULL", builder.toString(), "Inserting null char array should use nullText");
    }
}

package org.apache.commons.text;

import static org.junit.jupiter.api.Assertions.assertEquals;
import static org.junit.jupiter.api.Assertions.assertThrows;
import org.junit.jupiter.api.Test;

public class TextStringBuilderInsertTest {

    private static final String SEP = System.lineSeparator();
    private static final Object FOO = "foo";

    @Test
    public void testInsertOperations() {
        final TextStringBuilder sb = new TextStringBuilder();
        assertEquals("", sb.toString());

        // insert(int index, String str)
        sb.insert(0, "Hello");
        assertEquals("Hello", sb.toString());
        assertThrows(IndexOutOfBoundsException.class, () -> sb.insert(-1, "World"));
        assertThrows(IndexOutOfBoundsException.class, () -> sb.insert(6, "World"));

        // insert(int index, boolean value)
        sb.insert(5, true);
        assertEquals("Hellotrue", sb.toString());
        assertThrows(IndexOutOfBoundsException.class, () -> sb.insert(10, false));
        assertThrows(IndexOutOfBoundsException.class, () -> sb.insert(-20, false));

        // insert(int index, char value)
        sb.insert(9, 'A');
        assertEquals("HellotrueA", sb.toString());
        assertThrows(IndexOutOfBoundsException.class, () -> sb.insert(11, 'B'));
        assertThrows(IndexOutOfBoundsException.class, () -> sb.insert(-2, 'B'));

        // insert(int index, char[] chars)
        sb.insert(10, new char[]{'B', 'C'});
        assertEquals("HellotrueABC", sb.toString());
        assertThrows(IndexOutOfBoundsException.class, () -> sb.insert(13, new char[]{'D', 'E'}));
        assertThrows(IndexOutOfBoundsException.class, () -> sb.insert(-1, new char[]{'D', 'E'}));

        // insert(int index, char[] chars, int offset, int length)
        sb.insert(12, new char[]{'D', 'E', 'F'}, 1, 1); // Inserts 'E'
        assertEquals("HellotrueABCE", sb.toString());
        assertThrows(IndexOutOfBoundsException.class, () -> sb.insert(14, new char[]{'G', 'H', 'I'}, 1, 2));
        assertThrows(IndexOutOfBoundsException.class, () -> sb.insert(-1, new char[]{'G', 'H', 'I'}, 1, 1));

        // insert(int index, double value)
        sb.insert(13, 1.2d);
        assertEquals("HellotrueABCE1.2", sb.toString());
        assertThrows(IndexOutOfBoundsException.class, () -> sb.insert(17, 1.3d));
        assertThrows(IndexOutOfBoundsException.class, () -> sb.insert(-1, 1.3d));

        // insert(int index, float value)
        sb.insert(16, 1f);
        assertEquals("HellotrueABCE1.21.0", sb.toString());
        assertThrows(IndexOutOfBoundsException.class, () -> sb.insert(20, 1.3f));
        assertThrows(IndexOutOfBoundsException.class, () -> sb.insert(-3, 1.3f));

        // insert(int index, int value)
        sb.insert(19, 23);
        assertEquals("HellotrueABCE1.21.023", sb.toString());
        assertThrows(IndexOutOfBoundsException.class, () -> sb.insert(22, 20));
        assertThrows(IndexOutOfBoundsException.class, () -> sb.insert(-5, -5));

        // insert(int index, long value)
        sb.insert(21, 99L);
        assertEquals("HellotrueABCE1.21.02399", sb.toString());
        assertThrows(IndexOutOfBoundsException.class, () -> sb.insert(24, 22L));
        assertThrows(IndexOutOfBoundsException.class, () -> sb.insert(-1, -1L));

        // insert(int index, Object obj)
        sb.insert(23, FOO);
        assertEquals("HellotrueABCE1.21.02399foo", sb.toString());
        assertThrows(IndexOutOfBoundsException.class, () -> sb.insert(27, FOO));
        assertThrows(IndexOutOfBoundsException.class, () -> sb.insert(-3, FOO));
    }
}

