package org.apache.commons.text.similarity;

import org.junit.jupiter.api.Test;
import static org.junit.jupiter.api.Assertions.assertEquals;

public class LevenshteinDistanceSymmetryTest {

    @Test
    void testSymmetryOfLevenshteinDistance() {
        final LevenshteinDistance distance = new LevenshteinDistance();

        // Prepare two distinct text samples
        final CharSequence text1 = "kitten";
        final CharSequence text2 = "sitting";

        // Expected similarity score (edit distance)
        // k -> s (1)
        // e -> i (1)
        // (insert) g (1)
        final int expectedDistance = 3;

        // Submit both sequences to the similarity calculation service in forward order
        final int distance1 = distance.apply(text1, text2);

        // Submit both sequences to the similarity calculation service in reverse order
        final int distance2 = distance.apply(text2, text1);

        // Confirm that each comparison produces the same expected distance value
        assertEquals(expectedDistance, distance1, "Distance from text1 to text2 should be " + expectedDistance);
        assertEquals(expectedDistance, distance2, "Distance from text2 to text1 should be " + expectedDistance);

        // Verify the calculation remains symmetric regardless of which sequence is provided first
        assertEquals(distance1, distance2, "Levenshtein distance should be symmetric regardless of input order");
    }
}

