    private static Stream<Arguments> limitedDamerauLevenshteinDistanceTestCases() {
        return Stream.of(
                Arguments.of("abc", "abc", 0, 0),
                Arguments.of("abc", "abd", 1, 1),
                Arguments.of("abc", "axc", 0, -1), // distance is 1, exceeds threshold 0
                Arguments.of("ab", "ba", 1, 1), // transposition
                Arguments.of("ab", "ba", 0, -1), // transposition distance is 1, exceeds threshold 0
                Arguments.of("kitten", "sitting", 3, 3),
                Arguments.of("failing", "failings", 1, 1),
                Arguments.of("ca", "abc", 2, 2),
                Arguments.of("abc", "ca", 2, 2),
                Arguments.of(null, "a", 1, -1), // null left, distance likely exceeds threshold
                Arguments.of("a", null, 1, -1), // null right, distance likely exceeds threshold
                Arguments.of(null, null, 1, 0) // both null, distance is 0
        );
    }

    @ParameterizedTest(name = "DamerauLevenshteinDistance.limitedCompare(\"{0}\", \"{1}\") should return {3}")
    @MethodSource("limitedDamerauLevenshteinDistanceTestCases")
    void testLimitedCompare(final String left, final String right, final int threshold, final int expectedDistance) {
        final DamerauLevenshteinDistance instance = new DamerauLevenshteinDistance(threshold);
        final Integer leftRightDistance = instance.apply(left, right);
