    void setUp() {
        // Instantiate the calculator without a threshold limit
        calculator = new DamerauLevenshteinDistance();
    }

    @ParameterizedTest
    @CsvSource({
        " '', '', 0",
        " '', 'a', 1",
        " 'a', '', 1",
        " 'aa', 'aa', 0",
        " 'aa', 'a', 1",
        " 'a', 'aa', 1",
        " 'abc', 'abd', 1",
        " 'abd', 'abc', 1",
        " 'ab', 'ba', 1", // Transposition
        " 'topcoder', 'topcoder', 0",
        " 'damerau', 'damerua', 1", // Transposition
