package org.apache.commons.text.similarity;

import static org.junit.jupiter.api.Assertions.assertEquals;

import org.junit.jupiter.api.Test;

public class LevenshteinDistanceThresholdTest {

    @Test
    void testSymmetricThresholdDistance() {
        // Test case 1: Distance less than threshold
        final LevenshteinDistance distanceLessThanThreshold = new LevenshteinDistance(2);
        String s1_1 = "apple";
        String s1_2 = "aple"; // Distance 1
        assertEquals(1, distanceLessThanThreshold.apply(s1_1, s1_2), "Distance s1_1 to s1_2 should be 1");
        assertEquals(1, distanceLessThanThreshold.apply(s1_2, s1_1), "Distance s1_2 to s1_1 should be 1 (symmetric)");

        // Test case 2: Distance equal to threshold
        final LevenshteinDistance distanceEqualToThreshold = new LevenshteinDistance(1);
        String s2_1 = "kitten";
        String s2_2 = "sitten"; // Distance 1
        assertEquals(1, distanceEqualToThreshold.apply(s2_1, s2_2), "Distance s2_1 to s2_2 should be 1");
        assertEquals(1, distanceEqualToThreshold.apply(s2_2, s2_1), "Distance s2_2 to s2_1 should be 1 (symmetric)");

        // Test case 3: Distance greater than threshold
        final LevenshteinDistance distanceGreaterThanThreshold = new LevenshteinDistance(1);
        String s3_1 = "flaw";
        String s3_2 = "lawn"; // Distance 2
        assertEquals(-1, distanceGreaterThanThreshold.apply(s3_1, s3_2), "Distance s3_1 to s3_2 should be -1 (exceeds threshold)");
        assertEquals(-1, distanceGreaterThanThreshold.apply(s3_2, s3_1), "Distance s3_2 to s3_1 should be -1 (symmetric, exceeds threshold)");

        // Test case 4: Identical strings, distance 0, threshold > 0
        final LevenshteinDistance distanceIdentical = new LevenshteinDistance(5);
        String s4_1 = "hello";
        String s4_2 = "hello"; // Distance 0
        assertEquals(0, distanceIdentical.apply(s4_1, s4_2), "Distance identical strings should be 0");
        assertEquals(0, distanceIdentical.apply(s4_2, s4_1), "Distance identical strings should be 0 (symmetric)");

        // Test case 5: Empty strings and threshold
        final LevenshteinDistance distanceEmpty = new LevenshteinDistance(0);
        String s5_1 = "";
        String s5_2 = ""; // Distance 0
        assertEquals(0, distanceEmpty.apply(s5_1, s5_2), "Distance empty strings should be 0");
        assertEquals(0, distanceEmpty.apply(s5_2, s5_1), "Distance empty strings should be 0 (symmetric)");

        final LevenshteinDistance distanceEmptyThresholdOne = new LevenshteinDistance(1);
        String s6_1 = "a";
        String s6_2 = ""; // Distance 1
        assertEquals(1, distanceEmptyThresholdOne.apply(s6_1, s6_2), "Distance 'a' to '' should be 1");
        assertEquals(1, distanceEmptyThresholdOne.apply(s6_2, s6_1), "Distance '' to 'a' should be 1 (symmetric)");

        final LevenshteinDistance distanceEmptyThresholdZero = new LevenshteinDistance(0);
        String s7_1 = "a";
        String s7_2 = ""; // Distance 1
        assertEquals(-1, distanceEmptyThresholdZero.apply(s7_1, s7_2), "Distance 'a' to '' should be -1 (exceeds threshold)");
        assertEquals(-1, distanceEmptyThresholdZero.apply(s7_2, s7_1), "Distance '' to 'a' should be -1 (symmetric, exceeds threshold)");
    }
}

