package org.apache.commons.text.similarity;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.params.ParameterizedTest;
import org.junit.jupiter.params.provider.Arguments;
import org.junit.jupiter.params.provider.MethodSource;

import java.util.stream.Stream;

public class DamerauLevenshteinDistanceParameterizedTest {

    /**
     * Helper method to construct a {@link SimilarityInput} wrapper object.
     * Given the context of string inputs, this will create a {@code SimilarityInput<Character>}.
     * The {@code type} parameter is included to match the test description but is not
     * directly used in creating the {@code SimilarityInput} from a {@code CharSequence},
     * as {@code SimilarityInput.input(CharSequence)} inherently produces a {@code SimilarityInput<Character>}.
     *
     * @param <T> The expected element type of the SimilarityInput.
     * @param type The class type for the elements, expected to be Character.class for string inputs.
     * @param content The string content to wrap in a SimilarityInput.
     * @return A new {@code SimilarityInput} instance.
     */
    private static <T> SimilarityInput<T> createSimilarityInput(final Class<T> type, final String content) {
        // SimilarityInput.input(CharSequence) returns SimilarityInput<Character>.
        // We cast to SimilarityInput<T> for compile-time compatibility, assuming T is Character
        // as per the common use case for string similarity.
        return (SimilarityInput<T>) SimilarityInput.input(content);
    }

    /**
     * Provides test arguments for the Damerau-Levenshtein distance calculation.
     * Each argument set includes:
     * 1. The left string input.
     * 2. The right string input.
     * 3. An integer threshold for the DamerauLevenshteinDistance instance (or null for no threshold).
     * 4. The expected integer distance.
     * 5. The element type class (Character.class for string-based inputs).
     *
     * @return A stream of {@link Arguments} for parameterized testing.
     */
    private static Stream<Arguments> testDamerauLevenshteinDistanceCalculation() {
        return Stream.of(
            // left, right, threshold, expectedDistance, elementType
            Arguments.of("aa", "aa", null, 0, Character.class),
            Arguments.of("a", "b", null, 1, Character.class),
            Arguments.of("ab", "ac", null, 1, Character.class),
            Arguments.of("abc", "axc", null, 1, Character.class),
            Arguments.of("foo", "four", null, 2, Character.class),
            Arguments.of("apple", "aple", null, 1, Character.class), // deletion
            Arguments.of("aple", "apple", null, 1, Character.class), // insertion
            Arguments.of("kitten", "sitting", null, 3, Character.class), // substitution, substitution, insertion
            Arguments.of("niche", "chi", null, 4, Character.class), // delete n, i, e, transpose c,h.
            Arguments.of("CA", "AC", null, 1, Character.class), // transposition

            // Test with threshold
            Arguments.of("apple", "aple", 0, -1, Character.class), // distance 1 > threshold 0
            Arguments.of("apple", "aple", 1, 1, Character.class),
            Arguments.of("apple", "apply", 1, 1, Character.class),
            Arguments.of("apple", "apply", 0, -1, Character.class),
            Arguments.of("sitting", "kitten", 2, -1, Character.class), // distance 3 > threshold 2
            Arguments.of("sitting", "kitten", 3, 3, Character.class),
            Arguments.of("", "", null, 0, Character.class),
            Arguments.of("a", "", null, 1, Character.class),
            Arguments.of("", "a", null, 1, Character.class)
        );
    }

    /**
     * Validates the Damerau-Levenshtein distance calculation for similarity inputs,
     * ensuring bidirectional symmetry of the algorithm.
     *
     * @param <T> The type of elements in the SimilarityInput.
     * @param leftStr The left string for similarity comparison.
     * @param rightStr The right string for similarity comparison.
     * @param threshold An integer threshold for the DamerauLevenshteinDistance, or null for unlimited.
     * @param expectedDistance The expected Damerau-Levenshtein distance.
     * @param elementType The class type of the elements within the SimilarityInput (e.g., Character.class).
     */
    @ParameterizedTest
    @MethodSource
    public <T> void testDamerauLevenshteinDistanceCalculation(
            final String leftStr,
            final String rightStr,
            final Integer threshold,
            final Integer expectedDistance,
            final Class<T> elementType) {

        // Construct a new DamerauLevenshteinDistance instance configured with the provided threshold value.
        final DamerauLevenshteinDistance distance = new DamerauLevenshteinDistance(threshold);

        // Construct SimilarityInput wrapper objects for left and right inputs.
        final SimilarityInput<T> leftInput = createSimilarityInput(elementType, leftStr);
        final SimilarityInput<T> rightInput = createSimilarityInput(elementType, rightStr);

        // Apply the distance calculation for left-to-right.
        final Integer actualLeftToRightDistance = distance.apply(leftInput, rightInput);

        // Apply the distance calculation for right-to-left (inputs reversed).
        final Integer actualRightToLeftDistance = distance.apply(rightInput, leftInput);

        // Assert that the left-to-right distance matches the expected distance value.
        Assertions.assertEquals(expectedDistance, actualLeftToRightDistance,
                "Left-to-right distance mismatch for: \"" + leftStr + "\" and \"" + rightStr + "\" with threshold " + threshold);

        // Assert that the right-to-left distance also matches the expected distance value.
        Assertions.assertEquals(expectedDistance, actualRightToLeftDistance,
                "Right-to-left distance mismatch for: \"" + leftStr + "\" and \"" + rightStr + "\" with threshold " + threshold);
    }
}

package org.apache.commons.text.similarity;

import static org.junit.jupiter.api.Assertions.assertEquals;

import org.junit.jupiter.params.ParameterizedTest;
import org.junit.jupiter.params.provider.CsvSource;

class DamerauLevenshteinDistanceTest1 {

    @ParameterizedTest
    @CsvSource({
        "kitten, sitting, 3, 3",
        "saturday, sunday, 3, 3",
        "test, test, 0, 0",
        "test, tset, 1, 1", // Transposition
        "test, tes, 1, 1",  // Deletion
        "test, best, 1, 1", // Substitution
        "apple, aple, 1, 1", // Deletion, within threshold
        "apple, aple, 0, -1", // Deletion, threshold exceeded
        ", , 0, 0", // Both empty strings
        "a, , 1, 1", // Left empty
        ", a, 1, 1", // Right empty
        "CA, ABC, 3, 3", // Longer example
        "example, samples, 5, 5",
        "test, text, 1, 1",
        "marketing, markets, 3, 3",
        "book, back, 2, 2",
        "flaw, lawn, 3, 3"
    })
    void testDamerauLevenshteinDistanceSymmetry(
            final String left,
            final String right,
            final int threshold,
            final int expectedDistance) {
        final DamerauLevenshteinDistance calculator = new DamerauLevenshteinDistance(threshold);

        final Integer actualDistance1 = calculator.apply(left, right);
        final Integer actualDistance2 = calculator.apply(right, left);

        assertEquals(expectedDistance, actualDistance1,
                "Distance from '" + left + "' to '" + right + "' with threshold " + threshold + " was incorrect.");
        assertEquals(expectedDistance, actualDistance2,
                "Distance from '" + right + "' to '" + left + "' with threshold " + threshold + " (symmetry check) was incorrect.");
    }
}

package org.apache.commons.text.similarity;

import org.junit.jupiter.api.Test;
import static org.junit.jupiter.api.Assertions.assertEquals;

/**
 * Unit tests for {@link LevenshteinDistance} to validate its ability to accurately measure
 * the edit distance between two text sequences across a comprehensive range of scenarios,
 * including identical sequences, single character changes, multiple edits,
 * completely different sequences, and character transpositions.
 * It also verifies the symmetrical nature of the similarity measurement.
 */
public class LevenshteinDistanceTest1 {

    /**
     * Tests the {@link LevenshteinDistance} calculation for various input pairs,
     * ensuring correct edit distances and verifying symmetry.
     */
    @Test
    void testLevenshteinDistanceScenarios() {
        // Establish a text similarity calculator configured to operate without any distance limitations.
        final LevenshteinDistance distance = new LevenshteinDistance();

        // Scenario 1: Identical sequences - expect 0 modifications
        assertSymmetricalDistance(distance, "test", "test", 0, "Identical strings");
        assertSymmetricalDistance(distance, "", "", 0, "Both empty strings");

        // Scenario 2: Sequences differing by a single character change - expect 1 modification
        assertSymmetricalDistance(distance, "kitten", "sitten", 1, "Single substitution");
        assertSymmetricalDistance(distance, "flaw", "claw", 1, "Single substitution at start");

        // Scenario 3: Sequences requiring single character insertion - expect 1 modification
        assertSymmetricalDistance(distance, "sittin", "sitting", 1, "Single insertion");
        assertSymmetricalDistance(distance, "dog", "dogs", 1, "Single insertion at end");

        // Scenario 4: Sequences requiring single character deletion - expect 1 modification
        assertSymmetricalDistance(distance, "sitting", "sittin", 1, "Single deletion");
        assertSymmetricalDistance(distance, "dogs", "dog", 1, "Single deletion at end");

        // Scenario 5: Sequences requiring multiple edits
        assertSymmetricalDistance(distance, "sunday", "saturday", 3, "Multiple substitutions and one insertion");
        assertSymmetricalDistance(distance, "book", "back", 2, "Two substitutions");
        assertSymmetricalDistance(distance, "sitting", "kitten", 3, "Standard example: multiple edits");

        // Scenario 6: Completely different sequences - expect distance equal to length of longer string
        assertSymmetricalDistance(distance, "abc", "xyz", 3, "Completely different short strings");
        assertSymmetricalDistance(distance, "hello", "world", 4, "Completely different medium strings");

        // Scenario 7: Sequences involving empty strings with non-empty strings
        assertSymmetricalDistance(distance, "abc", "", 3, "One string empty, one non-empty");
        assertSymmetricalDistance(distance, "", "abc", 3, "One string empty, one non-empty (reversed)");

        // Scenario 8: Sequences involving character transpositions (Levenshtein treats as multiple edits)
        // Levenshtein distance does not have a specific 'transposition' operation;
        // it calculates them as a sequence of insertions and deletions/substitutions.
        assertSymmetricalDistance(distance, "ab", "ba", 2, "Simple transposition treated as 2 edits"); // a->b, b->a
        assertSymmetricalDistance(distance, "acb", "abc", 2, "Transposition within string treated as 2 edits"); // c->b, b->c
    }

    /**
     * Helper method to assert the calculated Levenshtein distance and verify its symmetry.
     *
     * @param distance The LevenshteinDistance instance.
     * @param left The first character sequence.
     * @param right The second character sequence.
     * @param expected The expected Levenshtein distance.
     * @param scenarioDescription A description of the test scenario.
     */
    private void assertSymmetricalDistance(
            final LevenshteinDistance distance,
            final CharSequence left,
            final CharSequence right,
            final int expected,
            final String scenarioDescription) {
        assertEquals(expected, distance.apply(left, right),
                String.format("Failed for scenario '%s': Distance from '%s' to '%s'", scenarioDescription, left, right));
        assertEquals(expected, distance.apply(right, left),
                String.format("Failed for scenario '%s' (Symmetry check): Distance from '%s' to '%s'", scenarioDescription, right, left));
    }
}

