package org.apache.commons.text.similarity;

import org.junit.jupiter.api.BeforeAll;
import org.junit.jupiter.params.ParameterizedTest;
import org.junit.jupiter.params.provider.Arguments;
import org.junit.jupiter.params.provider.MethodSource;

import java.lang.reflect.InvocationTargetException;
import java.util.stream.Stream;

import static org.junit.jupiter.api.Assertions.assertEquals;

/**
 * Tests {@link DamerauLevenshteinDistance} for its symmetry property across various {@link SimilarityInput} types.
 */
public class DamerauLevenshteinDistanceSymmetryTest {

    /**
     * The instance of {@link DamerauLevenshteinDistance} used for calculating distances.
     * Initialized once for all tests with default configuration.
     */
    private static DamerauLevenshteinDistance distanceCalculator;

    /**
     * Initializes the {@link DamerauLevenshteinDistance} calculator with its default configuration
     * before all test methods in this class are executed.
     */
    @BeforeAll
    static void setup() {
        distanceCalculator = new DamerauLevenshteinDistance();
    }

    /**
     * Utility helper method to wrap a {@link String} into a {@link SimilarityInput} object
     * of the specified type. This method assumes that the target {@link SimilarityInput}
     * implementation has a constructor that accepts a {@link CharSequence}.
     *
     * @param text The string to be wrapped into a {@link SimilarityInput}. Must not be null.
     * @param similarityInputType The {@link Class} object representing the desired
     *                            {@link SimilarityInput} implementation (e.g., {@code CharSequenceTokenizer.class}).
     * @return A new instance of the specified {@link SimilarityInput} type.
     * @throws RuntimeException if the {@link SimilarityInput} instance cannot be created,
     *                          for example, due to missing constructor or instantiation errors.
     */
    private static SimilarityInput createSimilarityInput(final String text, final Class<? extends SimilarityInput> similarityInputType) {
        try {
            // CharSequenceTokenizer is a concrete SimilarityInput that takes CharSequence.
            // Input 'text' is ensured to be non-null by the test case data.
            return similarityInputType.getConstructor(CharSequence.class).newInstance(text);
        } catch (final NoSuchMethodException | InstantiationException | IllegalAccessException | InvocationTargetException e) {
            throw new RuntimeException("Failed to create SimilarityInput instance of type " + similarityInputType.getName(), e);
        }
    }

    /**
     * Parameterized test method to validate the symmetry (commutative property) of the
     * {@link DamerauLevenshteinDistance} algorithm. It calculates the distance between two strings
     * in both forward and backward order and asserts that both results match the expected distance.
     *
     * @param leftString The first string input for the distance calculation.
     * @param rightString The second string input for the distance calculation.
     * @param expectedDistance The expected Damerau-Levenshtein distance between {@code leftString} and {@code rightString}.
     * @param similarityInputType The class type of the {@link SimilarityInput} to use for wrapping the strings.
     */
    @ParameterizedTest
    @MethodSource("symmetryTestCases")
    void testDamerauLevenshteinDistanceSymmetry(final String leftString, final String rightString, final int expectedDistance, final Class<? extends SimilarityInput> similarityInputType) {
        // Create SimilarityInput objects for both left and right strings
        final SimilarityInput leftInput = createSimilarityInput(leftString, similarityInputType);
        final SimilarityInput rightInput = createSimilarityInput(rightString, similarityInputType);

        // Extract CharSequence from SimilarityInput objects as required by DamerauLevenshteinDistance.apply
        final CharSequence leftCharSequence = leftInput.getCharSequence();
        final CharSequence rightCharSequence = rightInput.getCharSequence();

        // Compute the distance in the forward direction
        final Integer forwardDistance = distanceCalculator.apply(leftCharSequence, rightCharSequence);

        // Compute the distance in the backward (reversed) direction
        final Integer backwardDistance = distanceCalculator.apply(rightCharSequence, leftCharSequence);

        // Assert that the forward distance equals the expected distance
        assertEquals(expectedDistance, forwardDistance,
                "Forward distance calculation failed for ('" + leftString + "', '" + rightString + "')");

        // Assert that the backward distance also equals the expected distance (verifying symmetry)
        assertEquals(expectedDistance, backwardDistance,
                "Backward distance calculation failed for ('" + rightString + "', '" + leftString + "')");
    }

    /**
     * Provides a stream of {@link Arguments} for the parameterized test, covering various
     * string pairs, including valid pairs, empty strings, and boundary conditions.
     * <p>
     * Note: Test cases with literal {@code null} strings are deliberately excluded. The
     * {@link DamerauLevenshteinDistance#apply(CharSequence, CharSequence)} method throws
     * {@link NullPointerException} when provided with {@code null} {@link CharSequence} inputs,
     * which would conflict with the requirement to assert an integer {@code expectedDistance}.
     * </p>
     *
     * @return A {@link Stream} of {@link Arguments} containing test data.
     */
    private static Stream<Arguments> symmetryTestCases() {
        return Stream.of(
                // Valid string pairs
                Arguments.of("hello", "hallo", 1, CharSequenceTokenizer.class),
                Arguments.of("kitten", "sitting", 3, CharSequenceTokenizer.class),
                Arguments.of("Saturday", "Sunday", 3, CharSequenceTokenizer.class),
                Arguments.of("ca", "ac", 1, CharSequenceTokenizer.class), // Transposition
                Arguments.of("abc", "abc", 0, CharSequenceTokenizer.class), // Identical strings
                Arguments.of("a", "b", 1, CharSequenceTokenizer.class), // Single character substitution
                Arguments.of("a", "aa", 1, CharSequenceTokenizer.class), // Single character insertion
                Arguments.of("aa", "a", 1, CharSequenceTokenizer.class), // Single character deletion
                Arguments.of("example", "exmaple", 1, CharSequenceTokenizer.class), // Another transposition
                Arguments.of("algorithm", "altruism", 6, CharSequenceTokenizer.class),

                // Empty string scenarios
                Arguments.of("", "", 0, CharSequenceTokenizer.class),
                Arguments.of("a", "", 1, CharSequenceTokenizer.class),
                Arguments.of("", "a", 1, CharSequenceTokenizer.class),
                Arguments.of("abc", "", 3, CharSequenceTokenizer.class),
                Arguments.of("", "xyz", 3, CharSequenceTokenizer.class),

                // Boundary conditions
                Arguments.of("a", "a", 0, CharSequenceTokenizer.class), // Single identical character
                Arguments.of("thisisalongstring", "thisisanotherlongstring", 8, CharSequenceTokenizer.class), // Longer strings
                Arguments.of("abcdefghijklmnopqrstuvwxyz", "bcdefghijklmnopqrstuvwxyza", 2, CharSequenceTokenizer.class) // Shifted string
        );
    }
}

