public class OctalUnescaperInvalidOctalTest {

    @Test
    void testInvalidOctalSequencePreserved() {
        // Instantiate the Octal Unescaper translator
        final OctalUnescaper unescaper = new OctalUnescaper();

        // Prepare an input string containing a backslash followed by a character sequence
        // that does not form a valid octal pattern (specifically a backslash, the digit four,
        // and a non-digit character).
        final String input = "\\4G";

        // Invoke the translate method on the unescaper instance with this malformed input
        final String result = unescaper.translate(input);

        // Assert that the result matches the expected partially-processed string where the
        // invalid escape sequence is preserved in its original form with only the leading
        // backslash removed, confirming that the translator correctly handles malformed
        // octal sequences by treating them as literal characters rather than escape codes.
        final String expected = "4G";

        assertEquals(expected, result);
    }
}

