import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.BeforeAll;
import org.junit.jupiter.params.ParameterizedTest;
import org.junit.jupiter.params.provider.Arguments;
import org.junit.jupiter.params.provider.MethodSource;

import java.util.stream.Stream;

public class DamerauLevenshteinDistanceSimilarityInputTest {

    private static DamerauLevenshteinDistance defaultInstance;

    @BeforeAll
    static void setup() {
        defaultInstance = new DamerauLevenshteinDistance();
    }

    private static SimilarityInput<Object> buildSimilarityInput(final Class<?> cls, final String stringValue) {
        // The description specifies "constructs a SimilarityInput<Object> by invoking the static method
        // SimilarityInputTest.build(cls, stringValue)". Since 'SimilarityInputTest.build'
        // does not exist in the codebase, and SimilarityInput.input(CharSequence) is the
        // primary way to create a SimilarityInput from a String, we use that.
        // We cast to SimilarityInput<Object> to satisfy the return type as per description,
        // acknowledging this is an unchecked cast from SimilarityInput<Character>.
        // The 'cls' parameter is not directly used in this interpretation, as SimilarityInput.input(CharSequence)
        // implicitly works with Character elements.
        return (SimilarityInput<Object>) SimilarityInput.input(stringValue);
    }

    @ParameterizedTest(name = "DamerauLevenshteinDistance.unlimitedCompare(\"{0}\", \"{1}\") should return {2} ({3})")
    @MethodSource("unlimitedDamerauLevenshteinDistanceTestCases_SimilarityInput")
    void unlimitedDamerauLevenshteinDistanceTestCases(final String left, final String right, int expectedDistance, Class<?> cls) {
        final SimilarityInput<Object> leftInput = buildSimilarityInput(cls, left);
        final SimilarityInput<Object> rightInput = buildSimilarityInput(cls, right);

        final int leftRightDistance = defaultInstance.apply(leftInput, rightInput);
        Assertions.assertEquals(expectedDistance, leftRightDistance);

        final int rightLeftDistance = defaultInstance.apply(rightInput, leftInput);
        Assertions.assertEquals(expectedDistance, rightLeftDistance);
    }

    static Stream<Arguments> unlimitedDamerauLevenshteinDistanceTestCases_SimilarityInput() {
        return Stream.of(
            // exact match
            Arguments.of("a", "a", 0, Character.class),
            Arguments.of("", "", 0, Character.class),
            Arguments.of("abc", "abc", 0, Character.class),

            // insertion / deletion
            Arguments.of("a", "", 1, Character.class),
            Arguments.of("", "a", 1, Character.class),
            Arguments.of("abc", "", 3, Character.class),
            Arguments.of("", "abc", 3, Character.class),
            Arguments.of("abc", "ab", 1, Character.class),
            Arguments.of("ab", "abc", 1, Character.class),
            Arguments.of("a", "b", 1, Character.class),

            // substitution
            Arguments.of("a", "b", 1, Character.class),
            Arguments.of("ab", "ac", 1, Character.class),

            // transposition
            Arguments.of("ab", "ba", 1, Character.class),
            Arguments.of("abc", "acb", 1, Character.class),
            Arguments.of("abcdef", "abdcefg", 2, Character.class), // transposition + insertion
            Arguments.of("abcdef", "abdgecf", 3, Character.class), // transposition + 2 substitutions
            Arguments.of("abcdef", "abdefc", 2, Character.class), // 2 transpositions if not optimal string alignment
            Arguments.of("abcdef", "aebdcf", 3, Character.class) // transposition (eb) + transposition (dc)
        );
    }
}

