package org.apache.commons.text;

import org.junit.jupiter.api.Test;
import static org.junit.jupiter.api.Assertions.assertEquals;

/**
 * Tests for {@link StringEscapeUtils} focusing on correct handling of octal escape sequences
 * alongside various Unicode character encodings, including surrogate pairs and emojis.
 */
public class OctalEscapeAndUnicodePreservationTest {

    @Test
    void testOctalEscapesWithUnicodeCharacters() {
        // Scenario 1: Octal sequence followed by an isolated high surrogate marker
        // The system should convert the octal and preserve the high surrogate as is.
        final String input1 = "\\007\\uD83D"; // BEL (\u0007) followed by high surrogate
        final String expected1 = "\u0007\uD83D";
        assertEquals(expected1, StringEscapeUtils.unescapeJava(input1),
                "Scenario 1: Octal with isolated high surrogate should convert octal and preserve surrogate.");

        // Scenario 2: Octal sequence followed by a complete rocket emoji representation
        // The system should convert the octal and preserve the full emoji encoding.
        final String input2 = "\\007\\uD83D\\uDE80"; // BEL (\u0007) followed by rocket emoji
        final String expected2 = "\u0007\uD83D\uDE80";
        assertEquals(expected2, StringEscapeUtils.unescapeJava(input2),
                "Scenario 2: Octal with complete rocket emoji should convert octal and preserve emoji.");

        // Scenario 3: Octal sequence followed by a different complete emoji encoding
        // The system should convert the octal and preserve the full emoji encoding.
        final String input3 = "\\007\\uD83D\\uDCBB"; // BEL (\u0007) followed by laptop emoji
        final String expected3 = "\u0007\uD83D\uDCBB";
        assertEquals(expected3, StringEscapeUtils.unescapeJava(input3),
                "Scenario 3: Octal with complete laptop emoji should convert octal and preserve emoji.");

        // Scenario 4: Content ending with an octal sequence followed only by an isolated high surrogate
        // Verifies handling of an incomplete surrogate pair at the end of the string, which should remain unchanged.
        final String input4 = "Prefix\\007\\uD83D"; // Octal BEL and isolated high surrogate at the end
        final String expected4 = "Prefix\u0007\uD83D";
        assertEquals(expected4, StringEscapeUtils.unescapeJava(input4),
                "Scenario 4: Octal followed by isolated high surrogate at string end should preserve surrogate.");

        // Scenario 5: Octal sequence at the very beginning followed by various characters, ensuring correct parsing
        final String input5 = "\\007ABC"; // BEL (\u0007) followed by normal characters
        final String expected5 = "\u0007ABC";
        assertEquals(expected5, StringEscapeUtils.unescapeJava(input5),
                "Scenario 5: Octal at start of string with normal characters.");
    }
}

package org.apache.commons.text.similarity;

import org.junit.jupiter.params.ParameterizedTest;
import org.junit.jupiter.params.provider.Arguments;
import org.junit.jupiter.params.provider.MethodSource;

import java.util.stream.Stream;

import static org.junit.jupiter.api.Assertions.assertEquals;

public class LevenshteinDistanceThresholdTest {

    private static Stream<Arguments> provideTestCases() {
        return Stream.of(
            // left, right, threshold, expectedDistance
            Arguments.of("", "", 0, 0),
            Arguments.of("a", "a", 0, 0),
            Arguments.of("a", "b", 1, 1),
            Arguments.of("a", "b", 0, -1), // Distance 1, threshold 0, should return -1
            Arguments.of("abc", "axc", 1, 1),
            Arguments.of("abc", "axc", 0, -1), // Distance 1, threshold 0, should return -1
            Arguments.of("kitten", "sitting", 3, 3),
            Arguments.of("kitten", "sitting", 2, -1), // Distance 3, threshold 2, should return -1
            Arguments.of("distance", "difference", 5, 5),
            Arguments.of("distance", "difference", 4, -1), // Distance 5, threshold 4, should return -1
            Arguments.of("testing", "test", 3, 3),
            Arguments.of("test", "testing", 3, 3),
            Arguments.of("hello", "hallo", 1, 1),
            Arguments.of("hello", "hallo", 0, -1), // Distance 1, threshold 0, should return -1
            Arguments.of("Java", "Kotlin", 6, 6),
            Arguments.of("Java", "Kotlin", 5, -1) // Distance 6, threshold 5, should return -1
        );
    }

    @ParameterizedTest
    @MethodSource("provideTestCases")
    public void testSymmetricDistanceWithThreshold(final String left, final String right,
                                                   final Integer threshold, final Integer expectedDistance) {
        // Prepare the distance calculation service with a specific threshold value
        final LevenshteinDistance levenshteinDistance = new LevenshteinDistance(threshold);

        // Construct two comparable text inputs
        // Using Character as the generic type for SimilarityInput for string inputs
        final SimilarityInput<Character> leftInput = SimilarityInput.input(left);
        final SimilarityInput<Character> rightInput = SimilarityInput.input(right);

        // Calculate the similarity distance by comparing the first sequence against the second
        final Integer distanceLeftToRight = levenshteinDistance.apply(leftInput, rightInput);

        // Reverse the comparison order and calculate the distance from the second sequence to the first
        final Integer distanceRightToLeft = levenshteinDistance.apply(rightInput, leftInput);

        // Verify that both distance calculations produce identical numerical results
        assertEquals(expectedDistance, distanceLeftToRight, "Distance from left to right should match expected.");
        assertEquals(expectedDistance, distanceRightToLeft, "Distance from right to left should match expected.");

        // Confirming the calculation is order-independent and produces symmetric outcomes
        assertEquals(distanceLeftToRight, distanceRightToLeft, "Distances should be symmetric (order-independent).");
    }
}

