import org.junit.jupiter.api.Test;
import static org.junit.jupiter.api.Assertions.assertEquals;

public class OctalUnescaperTest1 {

    @Test
    void testOctalUnescaperWithSurrogatePairsAndEmojis() {
        final OctalUnescaper unescaper = new OctalUnescaper();

        // Test Case 1: Octal escape + high surrogate + trailing text
        final String input1 = "\\141\\uD83Dtext"; // '\141' is 'a'
        final String expected1 = "a\uD83Dtext";
        final String result1 = unescaper.translate(input1);
        assertEquals(expected1, result1, "Octal unescaping failed for high surrogate and trailing text.");

        // Test Case 2: Octal escape + complete emoji surrogate pair + additional text
        final String input2 = "\\142\\uD83D\\uDE00more text"; // '\142' is 'b', \uD83D\uDE00 is ð
        final String expected2 = "b\uD83D\uDE00more text";
        final String result2 = unescaper.translate(input2);
        assertEquals(expected2, result2, "Octal unescaping failed for emoji surrogate pair and additional text.");

        // Test Case 3: Octal escape + different emoji surrogate pair + text
        final String input3 = "\\143\\uD83C\\uDF08end"; // '\143' is 'c', \uD83C\uDF08 is ð
        final String expected3 = "c\uD83C\uDF08end";
        final String result3 = unescaper.translate(input3);
        assertEquals(expected3, result3, "Octal unescaping failed for different emoji surrogate pair and text.");

        // Test Case 4: Octal escape and a lone high surrogate without a corresponding low surrogate
        final String input4 = "\\144\\uD83E"; // '\144' is 'd'
        final String expected4 = "d\uD83E";
        final String result4 = unescaper.translate(input4);
        assertEquals(expected4, result4, "Octal unescaping failed for lone high surrogate.");
    }
}

