package org.apache.commons.text.similarity;

import static org.junit.jupiter.api.Assertions.assertArrayEquals;
import org.junit.jupiter.api.Test;

public class JaroWinklerSimilarityPrefixTest {

    @Test
    void shouldIdentifyPrefixContainmentCorrectly() {
        final CharSequence shorterSequence = "prefix";
        final CharSequence longerSequenceWithPrefix = "prefix_longer_string";

        // The 'matches' method in JaroWinklerSimilarity returns an array of three integers:
        // [0] = total number of matching characters
        // [1] = half transpositions (a metric used in Jaro-Winkler calculation, which should be 0 for a perfect prefix match)
        // [2] = common prefix length
        final int[] result = JaroWinklerSimilarity.matches(shorterSequence, longerSequenceWithPrefix);

        // For "prefix" vs "prefix_longer_string":
        // - Matching characters: 'p', 'r', 'e', 'f', 'i', 'x' (6 characters)
        // - Half transpositions: 0 (since "prefix" is a direct prefix of the second string, no characters are out of order relative to each other)
        // - Common prefix length: 6 (the entire "prefix" sequence)
        final int[] expected = {6, 0, 6};

        assertArrayEquals(expected, result, "The similarity metrics should correctly reflect prefix containment.");
    }
}

