package org.apache.commons.text.similarity;

import org.junit.jupiter.api.Test;
import static org.junit.jupiter.api.Assertions.assertArrayEquals;

public class LongestCommonSubsequenceProgressionTest {

    @Test
    void testProgressionMatrixWithSubsetStrings() {
        // Given a LongestCommonSubsequence instance
        final LongestCommonSubsequence lcs = new LongestCommonSubsequence();

        // Define the two text sequences, where one is a proper subset of the other
        final CharSequence left = "ab";
        final CharSequence right = "abc";

        // Expected progression matrix
        // The matrix size is (left.length() + 1) x (right.length() + 1)
        // For left="ab" (length 2) and right="abc" (length 3), the matrix will be 3x4.
        // Row 0 and Column 0 are for empty string comparisons, thus always 0.
        // The values represent the length of the LCS for prefixes up to that point.
        final int[][] expectedProgression = {
            {0, 0, 0, 0}, // Corresponds to comparing "" with "", "a", "ab", "abc"
            {0, 1, 1, 1}, // Corresponds to comparing "a" with "", "a", "ab", "abc"
            {0, 1, 2, 2}  // Corresponds to comparing "ab" with "", "a", "ab", "abc"
        };

        // Calculate the actual progression matrix
        // The method longestCommonSubstringLengthArray is deprecated, but it precisely
        // returns the "detailed progression matrix" as requested by the test description.
        // It provides the intermediate LCS lengths at each step of the dynamic programming.
        final int[][] actualProgression = lcs.longestCommonSubstringLengthArray(left, right);

        // Verify that the resulting progression structure accurately reflects the expected pattern:
        // no matches initially (row/col 0 are zeros), then a single character match
        // (first row after header showing 1s for 'a' match), followed by a two-character
        // match (second row after header showing 2s for 'ab' match).
        assertArrayEquals(expectedProgression[0], actualProgression[0], "Row 0 mismatch");
        assertArrayEquals(expectedProgression[1], actualProgression[1], "Row 1 mismatch");
        assertArrayEquals(expectedProgression[2], actualProgression[2], "Row 2 mismatch");
    }
}

