package org.apache.commons.text.lookup;

import static org.junit.jupiter.api.Assertions.assertThrows;

import java.io.IOException;
import java.nio.file.Files;
import java.nio.file.Path;

import org.junit.jupiter.api.Test;
import org.junit.jupiter.api.io.TempDir;

class XmlStringLookupSecurityTest {

    @TempDir
    Path tempDir; // JUnit 5 provides a temporary directory for test files

    @Test
    void testXmlExternalEntityResolutionRejected() throws IOException {
        // Prepare an XML document with an external entity reference (XXE payload)
        final String xmlContent = "<?xml version=\"1.0\"?>\n" +
                                  "<!DOCTYPE foo [\n" +
                                  "  <!ENTITY xxe SYSTEM \"file:///non/existent/path/to/secret.txt\">\n" +
                                  "]>\n" +
                                  "<root>\n" +
                                  "  <data>&xxe;</data>\n" +
                                  "</root>";

        final Path xmlFilePath = tempDir.resolve("xxe-document.xml");
        Files.write(xmlFilePath, xmlContent.getBytes());

        // Obtain an instance of XmlStringLookup configured with default security settings.
        // It is assumed that StringLookupFactory.xmlStringLookup() provides a securely
        // configured instance that prohibits external entity resolution by default.
        final StringLookup xmlLookup = StringLookupFactory.INSTANCE.xmlStringLookup();

        // Attempt to retrieve content that references the external entity.
        // The lookup key format for XmlStringLookup is typically "filePath:xpath".
        final String lookupKey = xmlFilePath.toAbsolutePath().toString() + ":/root/data";

        // Verify that the operation fails with an authorization error, typically an
        // IllegalArgumentException, confirming that the system correctly enforces
        // security policies by preventing potentially unsafe external entity expansion.
        assertThrows(IllegalArgumentException.class, () -> xmlLookup.lookup(lookupKey),
                     "XmlStringLookup should reject external entity resolution attempts due to security policies.");
    }
}

