import java.nio.file.Path;

import org.junit.jupiter.api.Test;

class XmlStringLookupSecurityTest {

    @Test
    void testXmlStringLookupThrowsExceptionOnExternalEntityWithSecureProcessing() throws IOException {
        // XML content with an external entity reference
        // This is designed to trigger external entity processing if it were enabled.
        final String xmlContent = "<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n" +
                                  "<!DOCTYPE foo [\n" +
                                  "  <!ENTITY xxe SYSTEM \"file:///non/existent/path/external.txt\">\n" +
                                  "]>\n" +
                                  "<root>\n" +
                                  "  <value>&xxe;</value>\n" +
                                  "</root>";

        Path tempXmlFile = null;
        try {
            // Create a temporary XML file
            tempXmlFile = Files.createTempFile("xxe_test", ".xml");
            Files.write(tempXmlFile, xmlContent.getBytes());

            // Construct the lookup key: URI to the temporary file + XPath expression
            final String lookupKey = tempXmlFile.toUri().toString() + ":/root/value";

            // Obtain the XmlStringLookup instance from the factory.
            // By default, the factory is expected to provide a secure instance with
            // external entity processing disabled.
            final StringLookup xmlStringLookup = StringLookupFactory.INSTANCE.xmlStringLookup();

            // Verify that invoking lookup with this key throws an IllegalArgumentException,
            // confirming that secure processing prevents external entity resolution.
            assertThrows(IllegalArgumentException.class, () -> xmlStringLookup.lookup(lookupKey),
                         "XmlStringLookup should throw IllegalArgumentException when processing external entities is disabled.");

        } finally {
            // Clean up the temporary file to ensure a clean test environment
            if (tempXmlFile != null && Files.exists(tempXmlFile)) {
                Files.delete(tempXmlFile);
            }
        }
    }
}

package org.apache.commons.text.lookup;

import static org.junit.jupiter.api.Assertions.assertEquals;
import static org.junit.jupiter.api.Assertions.assertNotNull;

import org.junit.jupiter.api.Test;

public class StringLookupFactoryClearCacheTest {

    @Test
    void testClearConstantStringLookupCache() {
        // Step 1: Populate the ConstantStringLookup cache.
        // We'll use a known constant that can be successfully looked up.
        final String key = "java.lang.Integer.MAX_VALUE";
        final String expectedValue = String.valueOf(Integer.MAX_VALUE);

        // Perform an initial lookup to ensure the cache is populated
        StringLookup constantLookup = StringLookupFactory.INSTANCE.constantStringLookup();
        String initialValue = constantLookup.lookup(key);
        assertNotNull(initialValue, "Initial lookup should not be null.");
        assertEquals(expectedValue, initialValue, "Initial lookup value should match expected.");

        // Step 2: Clear the global configuration cache for constant string resolution.
        StringLookupFactory.clear();

        // Step 3: Verify that the system returns to its initial state,
        // meaning that the constant can still be resolved, effectively
        // proving the cache was cleared and rebuilt on re-access.
        String valueAfterClear = constantLookup.lookup(key);
        assertNotNull(valueAfterClear, "Lookup after clear should not be null, meaning it was re-resolved.");
        assertEquals(expectedValue, valueAfterClear, "Lookup value after clear should still match expected.");
    }
}

