package org.apache.commons.text.diff;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.Test;

/**
 * Tests the equality assessment of {@link EditScript} instances to ensure they correctly
 * distinguish between different sets of transformation operations.
 */
public class EditScriptEqualityTest {

    @Test
    public void testEditScriptDistinguishesByOperationCounts() {
        // Scenario 1: Establish a text comparison outcome with identical counts across all transformation operations.
        // Comparing two identical strings results in an EditScript with only 'Keep' commands.
        final CharSequence text1A = "abcdef";
        final CharSequence text1B = "abcdef";
        final StringsComparator comparator1 = new StringsComparator(text11A, text1B);
        final EditScript<Character> script1 = comparator1.getEditScript();

        // Verify the expected counts for script1: all keeps, no inserts or deletes.
        Assertions.assertEquals(text1A.length(), script1.getKeep(),
            "Script 1 should have 'keep' operations equal to the length of the string.");
        Assertions.assertEquals(0, script1.getInserts(), "Script 1 should have no insert operations.");
        Assertions.assertEquals(0, script1.getDeletes(), "Script 1 should have no delete operations.");
        Assertions.assertEquals(0, script1.getModifications(), "Script 1 should have no modifications.");

        // Scenario 2: Establish a second text comparison outcome that differs in character substitutions and deletions.
        // Comparing "abcdef" with "abXdef" introduces one substitution (c -> X), which is typically
        // represented as one deletion ('c') and one insertion ('X') by StringsComparator.
        final CharSequence text2A = "abcdef";
        final CharSequence text2B = "abXdef";
        final StringsComparator comparator2 = new StringsComparator(text2A, text2B);
        final EditScript<Character> script2 = comparator2.getEditScript();

        // Verify the expected counts for script2: some keeps, one insert, one delete.
        Assertions.assertEquals(5, script2.getKeep(), "Script 2 should have 5 'keep' operations.");
        Assertions.assertEquals(1, script2.getInserts(), "Script 2 should have 1 insert operation.");
        Assertions.assertEquals(1, script2.getDeletes(), "Script 2 should have 1 delete operation.");
        Assertions.assertEquals(2, script2.getModifications(), "Script 2 should have 2 modifications (1 insert + 1 delete).");

        // Validate that the system recognizes these two outcomes (script1 and script2) as distinct and non-equivalent.
        // This confirms that EditScript's equality assessment properly detects differences in the underlying
        // transformation metrics (e.g., number of insertions and deletions).
        Assertions.assertNotEquals(script1, script2,
            "EditScripts with different operation counts (inserts/deletes) should not be considered equal.");

        // Sanity check: Ensure an identical script created from the same input is considered equal.
        final CharSequence text3A = "abcdef";
        final CharSequence text3B = "abcdef";
        final StringsComparator comparator3 = new StringsComparator(text3A, text3B);
        final EditScript<Character> script3 = comparator3.getEditScript();
        Assertions.assertEquals(script1, script3,
            "EditScripts generated from identical comparisons should be considered equal.");
    }
}

