package org.apache.commons.text;

import org.junit.jupiter.api.Test;
import static org.junit.jupiter.api.Assertions.assertEquals;

public class WordUtilsSeparatorTest {

    @Test
    void testWordSeparatorIdentification() {
        // Test Case 1: No custom separators defined (delimiters = null)
        // Expected behavior: Space is a separator, period is not.
        assertEquals("Hello.world", WordUtils.capitalize("hello.world", null),
                "When delimiters are null, a period should not be treated as a separator.");
        assertEquals("Hello World", WordUtils.capitalize("hello world", null),
                "When delimiters are null, a space should be treated as a separator.");

        // Test Case 2: Period explicitly excluded from separator list (empty char array)
        // Expected behavior: Neither space nor period should be a separator as the custom list is empty.
        assertEquals("Hello world", WordUtils.capitalize("hello world", new char[]{}),
                "When delimiters are an empty array, a space should not be treated as a separator.");
        assertEquals("Hello.world", WordUtils.capitalize("hello.world", new char[]{}),
                "When delimiters are an empty array, a period should not be treated as a separator.");

        // Test Case 3: Period included in custom separator set
        // Expected behavior: Period is a separator.
        assertEquals("Hello.World", WordUtils.capitalize("hello.world", new char[]{'.'}),
                "When '.' is explicitly included, it should be treated as a separator.");

        // Test Case 4: Multiple custom separators simultaneously
        // Sub-case 4a: Space not recognized when custom separator list contains only periods, underscores, and letters.
        assertEquals("Hello world", WordUtils.capitalize("hello world", new char[]{'.', '_', 'A', 'b'}),
                "When custom delimiters exclude space, space should not be a separator.");

        // Sub-case 4b: Period and underscore are recognized as separators when present in a custom list, including duplicates.
        assertEquals("Hello.World_Test", WordUtils.capitalize("hello.world_test", new char[]{'.', '_', 'A', '.', 'c'}),
                "When multiple custom delimiters are provided (including duplicates for '.'), both '.' and '_' should be separators.");
    }
}

package org.apache.commons.text;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.Test;

class WordUtilsDelimiterTest {

    @Test
    void testIsDelimiter() {
        // Scenario 1: Period character, null delimiter array -> false
        // Periods are not default whitespace delimiters
        Assertions.assertFalse(WordUtils.isDelimiter(Character.valueOf('.').hashCode(), null),
                "Period with null delimiters should be false (not a default whitespace delimiter)");

        // Scenario 2: Space character, null delimiter array -> true
        // Spaces are recognized as default whitespace delimiters
        Assertions.assertTrue(WordUtils.isDelimiter(Character.valueOf(' ').hashCode(), null),
                "Space with null delimiters should be true (a default whitespace delimiter)");

        // Scenario 3: Space character, single-element period array -> false
        // Space is not in the custom delimiter set {'.'}
        Assertions.assertFalse(WordUtils.isDelimiter(Character.valueOf(' ').hashCode(), new char[]{'.'}),
                "Space with custom delimiter {'.'} should be false");

        // Scenario 4: Period character, single-element period array -> true
        // Period matches the custom delimiter set {'.'}
        Assertions.assertTrue(WordUtils.isDelimiter(Character.valueOf('.').hashCode(), new char[]{'.'}),
                "Period with custom delimiter {'.'} should be true");

        // Scenario 5: Space character, multi-element array {'.', '_', 'a'} -> false
        // Space is absent from this custom delimiter set
        Assertions.assertFalse(WordUtils.isDelimiter(Character.valueOf(' ').hashCode(), new char[]{'.', '_', 'a'}),
                "Space with custom delimiters {'.', '_', 'a'} should be false");

        // Scenario 6: Period character, multi-element array {'.', '_', 'a', '.'} -> true
        // Period exists within the custom delimiter collection
        Assertions.assertTrue(WordUtils.isDelimiter(Character.valueOf('.').hashCode(), new char[]{'.', '_', 'a', '.'}),
