import org.junit.jupiter.api.Test;

class WordUtilsIsDelimiterTest {

    @Test
    void testIsDelimiterVariousConfigurations() {
        // Scenario 1: No custom separators defined (delimiters = null)
        // Expect '.' not to be a separator
        assertFalse(WordUtils.isDelimiter('.', null), "Period should not be a delimiter when no custom separators are defined.");
        // Expect ' ' to be a separator (default whitespace behavior)
        assertTrue(WordUtils.isDelimiter(' ', null), "Space should be a delimiter when no custom separators are defined.");

        // Scenario 2: Custom separator set with only a period
        final char[] dotSeparator = {'.'};
        // Expect ' ' not to be a separator
        assertFalse(WordUtils.isDelimiter(' ', dotSeparator), "Space should not be a delimiter when only '.' is a custom separator.");
        // Expect '.' to be a separator
        assertTrue(WordUtils.isDelimiter('.', dotSeparator), "Period should be a delimiter when it is the only custom separator.");

        // Scenario 3: Multiple custom separators: period, underscore, and 'a'
        final char[] multipleSeparators = {'.', '_', 'a'};
        // Expect ' ' not to be a separator
        assertFalse(WordUtils.isDelimiter(' ', multipleSeparators), "Space should not be a delimiter when custom separators are {'.', '_', 'a'}.");
        // Expect '.' to be a separator
        assertTrue(WordUtils.isDelimiter('.', multipleSeparators), "Period should be a delimiter when custom separators include it.");
        // Expect '_' to be a separator
        assertTrue(WordUtils.isDelimiter('_', multipleSeparators), "Underscore should be a delimiter when custom separators include it.");
        // Expect 'a' to be a separator
        assertTrue(WordUtils.isDelimiter('a', multipleSeparators), "Letter 'a' should be a delimiter when custom separators include it.");
        // Expect 'b' not to be a separator
        assertFalse(WordUtils.isDelimiter('b', multipleSeparators), "Letter 'b' should not be a delimiter when custom separators do not include it.");

        // Scenario 4: Custom separators with duplicates: period, underscore, 'a', and another period
        final char[] separatorsWithDuplicates = {'.', '_', 'a', '.'};
        // Expect '.' to be a separator (duplicate should not affect functionality)
        assertTrue(WordUtils.isDelimiter('.', separatorsWithDuplicates), "Period should be a delimiter even if it appears multiple times in the custom separator array.");
    }
}

