package org.apache.commons.text;

import org.junit.jupiter.api.Test;
import static org.junit.jupiter.api.Assertions.assertEquals;
import static org.junit.jupiter.api.Assertions.assertTrue;

import org.apache.commons.text.CharacterPredicate;
import org.apache.commons.text.CharacterPredicates;
import org.apache.commons.text.RandomStringGenerator;

import java.util.function.IntUnaryOperator;

public class RandomStringGeneratorReplacementTest {

    @Test
    void testFilteredByReplacement() {
        // To make the random string generation deterministic and always produce 'b',
        // we provide an IntUnaryOperator that returns the correct offset.
        // When withinRange('a', 'z') is used, minInclusive is 'a'.
        // To get 'b', the random number generator needs to effectively return 'b' - 'a' (which is 1).
        IntUnaryOperator deterministicRandom = bound -> {
            // The bound represents (maxInclusive - minInclusive + 1).
            // For the range 'a' through 'z', this bound will be 26.
            // We want the character 'b', which is at an offset of 1 from 'a'.
            return 'b' - 'a';
        };

        RandomStringGenerator.Builder builder = RandomStringGenerator.builder();

        // 1. Establish initial generator configuration: lowercase 'a' to 'z' and initial filtering rule.
        builder.withinRange('a', 'z')
               .filteredBy(CharacterPredicates.ASCII_LOWERCASE_LETTERS)
               .usingRandom(deterministicRandom); // Use deterministic random provider

        // 2. Replace the filtering rule with an alternative criterion that permits only the character 'b'.
        // Per RandomStringGenerator.Builder's Javadoc, "Multiple calls to this method
        // will replace the previously stored predicates."
        builder.filteredBy(codePoint -> codePoint == 'b');

        // Finalize the configuration and build the generator.
        RandomStringGenerator generator = builder.build();

        // 3. Produce a text sequence of one hundred characters.
        final int generatedLength = 100;
        String result = generator.generate(generatedLength);

        // 4. Examine each individual character to confirm all are 'b'.
        assertEquals(generatedLength, result.length(), "Generated string length must match the requested length.");
        for (final char c : result.toCharArray()) {
            assertEquals('b', c, "Each character in the generated string must be 'b' after the filter replacement.");
        }
    }
}

