package org.apache.commons.text;

import org.junit.jupiter.api.Test;
import static org.junit.jupiter.api.Assertions.assertEquals;

/**
 * Test class for validating the behavior of the RandomStringGenerator.Builder
 * when its character filter is replaced after initial configuration.
 */
public class for {

    /**
     * Character predicate that accepts only the character 'a'.
     */
    private static final CharacterPredicate PREDICATE_A = ch -> ch == 'a';

    /**
     * Character predicate that accepts only the character 'b'.
     */
    private static final CharacterPredicate PREDICATE_B = ch -> ch == 'b';

    /**
     * Tests that replacing a character filter on the RandomStringGenerator.Builder
     * completely overrides the previous filter.
     */
    @Test
    void testFilterReplacementOverridesPreviousFilter() {
        // Obtain a builder instance from the Random String Generator
        RandomStringGenerator.Builder builder = RandomStringGenerator.builder();

        // Configure it to generate characters within the lowercase alphabetic range from 'a' to 'z'
        builder.withinRange('a', 'z');

        // Apply the first character predicate filter to restrict output to characters matching 'a'
        builder.withCharacterFilter(PREDICATE_A);

        // Invoke the filter replacement method on the builder with the second character predicate
        // to override the previously configured filter, restricting output to 'b'
        builder.withCharacterFilter(PREDICATE_B);

        // Build the generator instance
        RandomStringGenerator generator = builder.build();

        // Invoke its generation method to produce a string of one hundred characters
        String generatedString = generator.generate(100);

        // Assert that the generated string has the expected length
        assertEquals(100, generatedString.length(), "Generated string length should be 100 characters.");

        // Iterate through each character and assert that every character in the result equals 'b'
        // to confirm that the second filter completely replaced the first filter.
        for (char ch : generatedString.toCharArray()) {
            assertEquals('b', ch, "All characters in the generated string should be 'b' after filter replacement.");
        }
    }
}

