import org.junit.jupiter.api.Test;

public class RandomStringGeneratorPasswordTest {

    // A comprehensive set of punctuation characters based on common ASCII punctuation
    private static final char[] PUNCTUATION_CHARS = {
        '!', '@', '#', '$', '%', '^', '&', '*', '(', ')', '-', '_', '=', '+', '[', ']', '{', '}', ';', ':', ',', '.', '<', '>', '/', '?', '\\', '|', '`', '~'
    };

    @Test
    void testGenerateSecurePasswordSequence() {
        final int passwordLength = 10;

        // Create a set of all allowed characters for verification
        final Set<Character> allowedChars = new HashSet<>();
        for (char c = 'a'; c <= 'z'; c++) {
            allowedChars.add(c);
        }
        for (char c = 'A'; c <= 'Z'; c++) {
            allowedChars.add(c);
        }
        for (char c = '0'; c <= '9'; c++) {
            allowedChars.add(c);
        }
        for (char c : PUNCTUATION_CHARS) {
            allowedChars.add(c);
        }

        final RandomStringGenerator generator = RandomStringGenerator.builder()
            .setAccumulate(true) // Accumulate characters from all specified ranges and selections
            .withinRange(new char[][]{ // Add lowercase, uppercase, and digits
                {'a', 'z'},
                {'A', 'Z'},
                {'0', '9'}
            })
            .selectFrom(PUNCTUATION_CHARS) // Add punctuation characters
            .build();

        final String password = generator.generate(passwordLength);

        // Verify the length of the generated password
        assertTrue(password.length() == passwordLength, "Generated password must be 10 characters long");

        // Verify that every character in the generated password belongs to one of the allowed categories
        for (char c : password.toCharArray()) {
            assertTrue(allowedChars.contains(c),
                "Character '" + c + "' is not an allowed lowercase, uppercase, digit, or punctuation character.");
        }
    }
}

