package org.apache.commons.text;

import java.util.Arrays;
import java.util.HashSet;
import java.util.Set;
import java.util.function.IntUnaryOperator;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.ParameterizedTest;
import org.junit.jupiter.params.provider.ValueSource;

public class RandomStringGeneratorParameterizedTest {

    @ParameterizedTest
    @ValueSource(booleans = {true, false})
    void testAccumulationBehavior(final boolean accumulate) {
        final String referenceString = "abcde";
        final Set<Character> referenceChars = new HashSet<>();
        for (final char c : referenceString.toCharArray()) {
            referenceChars.add(c);
        }

        // Custom deterministic random operator that always returns 0.
        // This will effectively pick the first available character or the minimum code point.
        final IntUnaryOperator deterministicRandom = bound -> 0;

        final RandomStringGenerator generator = RandomStringGenerator.builder()
            .setAccumulate(accumulate)
            .usingRandom(deterministicRandom)
            // Initial range: 'a' to 'e'
            .withinRange(new char[][]{{'a', 'e'}})
            // Progressively decreasing character sets
            .selectFrom('a', 'b', 'c', 'd')
            .withinRange(new char[][]{{'a', 'c'}})
            .selectFrom('a', 'b')
            .selectFrom('a')
            // Calls with null and empty arguments to verify robustness
            .selectFrom((char[]) null) // This will clear if accumulate is false, or do nothing if accumulate is true
            .selectFrom(new char[0])   // This will clear if accumulate is false, or do nothing if accumulate is true
            .build();

        final int generatedLength = 10;
        final String generatedString = generator.generate(generatedLength);

        Assertions.assertEquals(generatedLength, generatedString.length(), "Generated string length must match requested length.");

        if (accumulate) {
            // When accumulate is true, all characters from 'a' to 'e' are part of the character pool.
            // Due to the deterministicRandom always returning 0, the character selected would be the first character in the internal
            // characterList, which is derived from the accumulated characterSet {'a', 'b', 'c', 'd', 'e'}.
            // The exact character is not predictable due to HashSet's unordered nature when converting to ArrayList,
            // but it must be one of 'a' through 'e'.
            for (final char c : generatedString.toCharArray()) {
                Assertions.assertTrue(referenceChars.contains(c), "Character '" + c + "' should be in reference string when accumulate is true.");
            }
        } else {
            // When accumulate is false, each call replaces the character set.
            // The last effective call is .selectFrom(new char[0]).
            // This clears the internal characterSet in the builder, making it empty.
            // The RandomStringGenerator will then use its default minCodePoint (0) and maxCodePoint (Character.MAX_CODE_POINT).
            // With deterministicRandom always returning 0, the generated code point will be 0 ('\u0000').
            // The character '\u0000' is not present in "abcde".
            for (final char c : generatedString.toCharArray()) {
                Assertions.assertFalse(referenceChars.contains(c), "Character '" + c + "' should NOT be in reference string when accumulate is false.");
            }
        }
    }
}

package org.apache.commons.text;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.Test;

import java.util.Arrays;

public class RandomStringGeneratorMultiRangeAndPunctuationTest {

    @Test
    void testGenerateWithMultipleRangesAndPunctuation() {
        // Begin by constructing a sorted character array containing specific punctuation symbols
        char[] punctuation = {
            '!', '@', '#', '$', '%', '^', '&', '*', '(', ')', '-', '_', '+', '=',
            '[', ']', '{', '}', ';', ':', ',', '.', '<', '>', '/', '?', '\\', '|', '`', '~'
        };
        Arrays.sort(punctuation); // Sort for efficient binary search validation

        // Build a Random String Generator instance using its builder interface,
        // configuring it to accumulate character sources across multiple range definitions
        // by enabling accumulation mode.
        RandomStringGenerator generator = RandomStringGenerator.builder()
            .setAccumulate(true) // Enable accumulation mode
            // Specify three alphanumeric ranges covering lowercase letters, uppercase letters, and digits
            .withinRange('a', 'z') // lowercase letters
            .withinRange('A', 'Z') // uppercase letters
            .withinRange('0', '9') // digits
            // Followed by selecting from the punctuation array as an additional source
            .selectFrom(punctuation)
            .build();

        // Generate a random string of fixed length using the configured generator.
        int fixedLength = 100;
        String generatedString = generator.generate(fixedLength);

        // Assert that the generated string has the expected length
        Assertions.assertEquals(fixedLength, generatedString.length(),
            "Generated string length should match the requested fixed length.");

        // Upon generation, iterate through each character in the resulting string and validate
        // that every character satisfies at least one of the configured constraints by
        // verifying it is either a letter, a digit, or present within the punctuation array
        // using binary search.
        for (char c : generatedString.toCharArray()) {
            boolean isLetterOrDigit = Character.isLetterOrDigit(c);
            boolean isPunctuation = Arrays.binarySearch(punctuation, c) >= 0;

            Assertions.assertTrue(isLetterOrDigit || isPunctuation,
                "Character '" + c + "' (U+" + Integer.toHexString(c) + ") " +
                "did not match any of the allowed character ranges or punctuation set.");
        }
    }
}

package org.apache.commons.text;

import org.junit.jupiter.params.ParameterizedTest;
import org.junit.jupiter.params.provider.ValueSource;
import static org.junit.jupiter.api.Assertions.assertEquals;

public class NewRandomStringGeneratorTest {

    @ParameterizedTest
    @ValueSource(booleans = {false, true})
    void testSelectFromAccumulation(final boolean accumulate) {
        final String str = "abcde";

        // Based on the source code of RandomStringGenerator.Builder.selectFrom(char... chars),
        // selectFrom(null) and selectFrom() are treated as adding no characters.
        // If accumulate is false, each selectFrom call resets the character set.
        // Therefore, if accumulate is false and the chain ends with selectFrom(null) or selectFrom(),
        // the character set becomes empty.
        // An empty character set causes generate(10) to fall back to default unicode range,
        // which makes the assertion unreliable for accumulate=false.
        // However, the instructions require implementing the described behavior directly.
        final RandomStringGenerator generator = RandomStringGenerator.builder()
                .setAccumulate(accumulate)
                .selectFrom('a', 'b', 'c', 'd', 'e')
                .selectFrom('a', 'b', 'c', 'd')
                .selectFrom('a', 'b', 'c')
                .selectFrom('a', 'b')
                .selectFrom(null) // As per RandomStringGenerator.Builder source, treated as no-op for adding chars.
                .selectFrom()    // As per RandomStringGenerator.Builder source, treated as no-op for adding chars.
                .get();

        final String randomText = generator.generate(10);

        for (final char c : randomText.toCharArray()) {
            assertEquals(accumulate, str.indexOf(c) != -1);
        }
    }
}

