package org.apache.commons.text.lookup;

import static org.junit.jupiter.api.Assertions.assertThrows;
import static org.junit.jupiter.api.Assertions.assertTrue;

import java.io.IOException;
import java.nio.file.Files;
import java.nio.file.Path;
import java.nio.file.Paths;

import org.apache.commons.text.StringSubstitutor;
import org.junit.jupiter.api.Test;

/**
 * Test case for validating that the interpolation system correctly rejects attempts to process
 * external document type definitions (DTDs) by default for security reasons when using XmlStringLookup.
 */
class XmlStringLookupExternalDtdRejectionTest {

    @Test
    void testXmlLookupRejectsExternalDtdByDefault() throws IOException {
        // 1. Define the content for an XML file that includes an external DTD reference.
        // This reference is intentionally pointing to a non-existent local file to simulate
        // an attempt to load an external entity, which should be blocked by default.
        final String xmlContentWithExternalDtd = "<!DOCTYPE foo SYSTEM \"file:///non/existent/path/malicious.dtd\">" +
                                                 "<foo>bar</foo>";

        // 2. Create a temporary XML file in a test-specific resources directory.
        // This file will serve as the "external document" that the interpolation system attempts to process.
        final Path testResourcesDir = Paths.get("target/test-classes/org/apache/commons/text/lookup/");
        Files.createDirectories(testResourcesDir);
        final Path tempXmlFile = testResourcesDir.resolve("external-dtd-test.xml");
        Files.write(tempXmlFile, xmlContentWithExternalDtd.getBytes());

        try {
            // 3. Configure the interpolation system to operate in interpolation mode.
            // StringSubstitutor.createInterpolator() is expected to provide a securely configured system
            // that includes XmlStringLookup, with external DTD processing disabled by default.
            final StringSubstitutor interpolator = StringSubstitutor.createInterpolator();

            // 4. Construct a substitution string that uses the 'xml' lookup to reference the temporary XML file.
            // The lookup key points to the absolute path of the XML file and attempts to extract an XPath expression.
            // The crucial part is that processing this XML should trigger the DTD rejection mechanism.
            final String lookupKey = "${xml:" + tempXmlFile.toAbsolutePath().toString() + "::/foo}";

            // 5. Verify the system refuses this operation by confirming it raises an argument validation failure.
            // This asserts that the interpolation mechanism effectively blocks external entity processing
            // to prevent potential security vulnerabilities from untrusted content sources.
            final Exception thrown = assertThrows(IllegalArgumentException.class, () ->
                interpolator.replace(lookupKey),
                "Expected IllegalArgumentException due to external DTD rejection, but none was thrown."
            );

            // Further validate the exception message for clarity, expecting it to contain terms
            // related to DOCTYPE, external entity, or security, indicating the reason for the rejection.
            final String message = thrown.getMessage();
            assertTrue(message != null &&
                       (message.contains("DOCTYPE") ||
                        message.contains("external entity") ||
                        message.contains("security")),
                       "Exception message did not clearly indicate DTD or external entity rejection. Actual message: " + message);

        } finally {
            // Ensure the temporary file is deleted after the test, regardless of its outcome.
            Files.deleteIfExists(tempXmlFile);
        }
    }
}

package org.apache.commons.text.lookup;

import org.apache.commons.text.StringSubstitutor;
import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.Test;

public class InterpolatorStringLookupSecurityTest {

    @Test
    void testExternalDocumentEntityRejection() {
        // Description: Test to validate that the system's variable substitution mechanism properly rejects attempts
        // to process external document references through its interpolation feature, specifically ensuring that
        // when a user attempts to use the interpolator to resolve a variable expression containing a reference
        // to an external document entity, the system recognizes this as a security violation and immediately
        // fails the operation by raising an authorization error, thereby preventing potential unauthorized
        // access to external resources or document content that could be exploited to read sensitive information
        // from the file system, with the test confirming that the interpolation process does not proceed with the
        // substitution and instead halts execution with the appropriate error condition, using JUnit 5 as the
        // testing framework and standard exception assertion capabilities to verify the system throws an illegal
        // argument error when encountering the external entity reference pattern.

        // The StringSubstitutor.createInterpolator() method provides an InterpolatorStringLookup that should
        // be configured to prevent XXE (XML External Entity) attacks.
        final StringSubstitutor interpolator = StringSubstitutor.createInterpolator();

        // Craft an XML payload that attempts to define and resolve an external entity
        // pointing to a local file (e.g., pom.xml). This simulates an XXE attack.
        final String xxePayload = "<?xml version="1.0" encoding="UTF-8"?>" +
                                  "<!DOCTYPE foo [" +
                                  "  <!ENTITY xxe SYSTEM "file:./pom.xml">" + // Attempt to read pom.xml
                                  "]>" +
                                  "<foo>&xxe;</foo>";

        // The full string to be interpolated using the 'xml' lookup
        final String inputWithXxe = "${xml:" + xxePayload + "}";

        // Assert that attempting to interpolate this string with the XXE payload results in an IllegalArgumentException.
        // This verifies the security mechanism is in place to reject such attempts.
        Assertions.assertThrows(
            IllegalArgumentException.class,
            () -> interpolator.replace(inputWithXxe),
            "Expected IllegalArgumentException for XXE payload via xml lookup."
        );

        // A second scenario attempting to reference an external DTD.
        // The prompt states "external document entity", implying a general rejection of the pattern
        // that attempts to fetch external content for security reasons.
        final String externalDtdReference = "<?xml version="1.0" encoding="UTF-8"?>" +
                                            "<!DOCTYPE document SYSTEM "document-external-dtd.dtd">" +
                                            "<document>&external;</document>"; // &external; would be defined in the DTD

        final String inputWithExternalDtd = "${xml:" + externalDtdReference + "}";

        Assertions.assertThrows(
            IllegalArgumentException.class,
            () -> interpolator.replace(inputWithExternalDtd),
            "Expected IllegalArgumentException for external DTD reference via xml lookup."
        );
    }
}

