package org.apache.commons.text.lookup;

import org.apache.commons.text.StringSubstitutor;
import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.Test;

import java.io.File;
import java.nio.file.Paths;

/**
 * Tests for XML entity resolution within StringSubstitutor with external DTD access enabled.
 */
class XmlStringLookupExternalEntityResolutionTest {

    private static final String EXPECTED_EXTERNAL_ENTITY_VALUE = "Hello External World!";
    private static final String TEST_RESOURCES_PATH = "src/test/resources/org/apache/commons/text/";
    private static final String XML_DOCUMENT_NAME = "document-external-dtd.xml";
    private static final String XML_XPATH_SELECTOR = "/document";

    @Test
    void testXmlLookupWithExternalEntityResolution() {
        // Store original system property for restoration
        final String originalAccessExternalDTD = System.getProperty("javax.xml.accessExternalDTD");

        try {
            // Explicitly enable external DTD access
            System.setProperty("javax.xml.accessExternalDTD", "all");

            // Construct the absolute path to the XML document
            // Assuming the test runs from the project root directory.
            final String xmlDocumentPath = Paths.get(TEST_RESOURCES_PATH, XML_DOCUMENT_NAME).toAbsolutePath().toString();

            // Instantiate StringSubstitutor configured with the default interpolator lookup
            final StringSubstitutor substitutor = StringSubstitutor.createInterpolator();

            // Construct an interpolation expression string that references an XML lookup
            // targeting a document path containing an entity reference with an XPath selector.
            final String interpolationExpression = "${xml:" + xmlDocumentPath + "::" + XML_XPATH_SELECTOR + "}";

            // Invoke the replace method to trigger XML parsing and entity resolution
            final String result = substitutor.replace(interpolationExpression);

            // Trim any whitespace from the returned result
            final String trimmedResult = result != null ? result.trim() : null;

            // Assert that the trimmed output equals the expected data constant
            // to confirm that external entity processing occurred despite default security restrictions.
            Assertions.assertEquals(EXPECTED_EXTERNAL_ENTITY_VALUE, trimmedResult,
                "The XML lookup should have resolved the external entity correctly.");

        } finally {
            // Restore the original system property
            if (originalAccessExternalDTD != null) {
                System.setProperty("javax.xml.accessExternalDTD", originalAccessExternalDTD);
            } else {
                System.clearProperty("javax.xml.accessExternalDTD");
            }
        }
    }
}

