package org.apache.commons.dbcp2.datasources;

import org.junit.jupiter.api.Test;
import static org.junit.jupiter.api.Assertions.assertEquals;
import static org.junit.jupiter.api.Assertions.assertNull;
import static org.junit.jupiter.api.Assertions.assertNotNull;

/**
 * Tests for UserPassKey.
 */
public class UserPassKeyTest1 {

    @Test
    public void testClear() {
        // Scenario 1: Missing credentials
        UserPassKey missingCredentials = new UserPassKey((String) null, (String) null);
        missingCredentials.clear();
        assertNull(missingCredentials.getUserName());
        assertNull(missingCredentials.getPassword());

        // Scenario 2: Empty credentials
        UserPassKey emptyCredentials = new UserPassKey("", "");
        emptyCredentials.clear();
        assertNotNull(emptyCredentials.getUserName());
        assertEquals("", emptyCredentials.getUserName());
        assertNotNull(emptyCredentials.getPassword());
        // After clearing a non-null char array, it's filled with null characters, so converting back to string results in a string of that length
        assertEquals(new String(new char[0]), emptyCredentials.getPassword());

        // Scenario 3: Populated credentials
        UserPassKey populatedCredentials = new UserPassKey("testuser", "testpassword");
        populatedCredentials.clear();
        assertNotNull(populatedCredentials.getUserName());
        assertEquals(new String(new char["testuser".length()]), populatedCredentials.getUserName());
        assertNotNull(populatedCredentials.getPassword());
        assertEquals(new String(new char["testpassword".length()]), populatedCredentials.getPassword());
    }
}

package org.apache.commons.dbcp2;

