/**
 * Tests that the connection management system correctly updates credential information.
 */
public class ConnectionManagementTest {

    /**
     * Validates that the connection factory correctly updates the password when a new password is provided.
     */
    @Test
    public void testPasswordUpdate() {
        final BasicDataSource dataSource = new BasicDataSource();
        dataSource.setUsername("testuser");
        dataSource.setPassword("initialPassword");
        dataSource.setDefaultQueryTimeout(Integer.valueOf(1000));
        dataSource.setValidationQuery("SELECT 1");

        final String newPassword = "newPassword";
        dataSource.setPassword(newPassword);

        assertEquals(newPassword, dataSource.getPassword());
    }
}

