package org.apache.commons.dbcp2.datasources;

import org.junit.jupiter.api.AfterEach;
import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.Test;

import static org.junit.jupiter.api.Assertions.assertNull;
import static org.junit.jupiter.api.Assertions.assertTrue;
import static org.junit.jupiter.api.Assertions.assertFalse;

/**
 * Test case for the read-only configuration of PerUserPoolDataSource.
 */
public class TestPerUserPoolDataSourceReadOnly {

    private PerUserPoolDataSource ds;

    @BeforeEach
    public void setUp() {
        ds = new PerUserPoolDataSource();
    }

    @AfterEach
    public void tearDown() throws Exception {
        ds.close();
    }

    @Test
    public void testReadOnlyLifecycle() {
        // 1. Initial state: No read-only preference set. Verify it's absent.
        assertNull(ds.getDefaultReadOnly(), "DefaultReadOnly should be null initially");

        // 2. Set to true: Enable read-only. Verify it's enabled.
        ds.setDefaultReadOnly(Boolean.TRUE);
        assertTrue(ds.getDefaultReadOnly(), "DefaultReadOnly should be true");

        // 3. Set to false: Disable read-only. Verify it's disabled.
        ds.setDefaultReadOnly(Boolean.FALSE);
        assertFalse(ds.getDefaultReadOnly(), "DefaultReadOnly should be false");

        // 4. Set to true again: Re-enable read-only. Verify it's enabled.
        ds.setDefaultReadOnly(Boolean.TRUE);
        assertTrue(ds.getDefaultReadOnly(), "DefaultReadOnly should be true again");

        // 5. Set to null: Clear the preference. Verify it's absent again.
        ds.setDefaultReadOnly(null);
        assertNull(ds.getDefaultReadOnly(), "DefaultReadOnly should be null after clearing");
    }
}

package org.apache.commons.dbcp2.datasources;

