/**
 * Tests for {@link CharArray}.
 */
public class CharArrayTest1 {

    @Test
    public void testClear() {
        // 1. Test clearing a CharArray initialized with null
        final CharArray nullCharArray = new CharArray((String) null);
        nullCharArray.clear();
        assertNull(nullCharArray.get(), "Clearing a null-initialized CharArray should keep it null.");

        // 2. Test clearing a CharArray initialized with an empty string
        final CharArray emptyCharArray = new CharArray("");
        emptyCharArray.clear();
        assertArrayEquals(new char[0], emptyCharArray.get(), "Clearing an empty CharArray should result in an empty char array.");

        // 3. Test clearing a CharArray initialized with a non-empty string
        final CharArray filledCharArray = new CharArray("abc");
        filledCharArray.clear();
        final char[] expected = new char[3]; // Expects an array of null characters
        assertArrayEquals(expected, filledCharArray.get(), "Clearing a non-empty CharArray should zero it out while preserving length.");
    }
}

