package org.apache.commons.dbcp2.datasources;

import org.junit.jupiter.api.Test;

import java.util.Arrays;

import static org.junit.jupiter.api.Assertions.assertArrayEquals;
import static org.junit.jupiter.api.Assertions.assertNull;

/**
 * Tests for {@link CharArray}.
 */
public class CharArrayTest1 {

    @Test
    public void testClear() {
        // Scenario 1: Holder initialized with no content
        final CharArray nullHolder = new CharArray((char[]) null);
        nullHolder.clear();
        assertNull(nullHolder.get(), "Clearing a null holder should result in null content.");

        // Scenario 2: Holder initialized with an empty sequence
        final CharArray emptyHolder = new CharArray(new char[0]);
        emptyHolder.clear();
        assertArrayEquals(new char[0], emptyHolder.get(), "Clearing an empty holder should result in an empty char array.");

        // Scenario 3: Holder with sensitive data
        final char[] sensitiveData = "sensitiveData".toCharArray();
        final CharArray dataHolder = new CharArray(sensitiveData);
        dataHolder.clear();
        final char[] clearedData = dataHolder.get();
        final char[] expected = new char[sensitiveData.length];
        Arrays.fill(expected, '\0');
        assertArrayEquals(expected, clearedData, "Clearing should replace all characters with null characters while preserving length.");
    }
}

package org.apache.commons.dbcp2.datasources;

import org.junit.jupiter.api.Test;

import static org.junit.jupiter.api.Assertions.assertFalse;
import static org.junit.jupiter.api.Assertions.assertNull;
import static org.junit.jupiter.api.Assertions.assertTrue;

/**
 * Tests the default read-only property lifecycle of the PerUserPoolDataSource.
 */
public class PerUserPoolDataSourceReadOnlyTest {

    @Test
    public void testDefaultReadOnlyLifecycle() throws Exception {
        try (final PerUserPoolDataSource ds = new PerUserPoolDataSource()) {
            // 1. Initial state should be null
            assertNull(ds.isDefaultReadOnly(), "Default read-only should initially be null.");

            // 2. Set to true
            ds.setDefaultReadOnly(Boolean.TRUE);
            assertTrue(ds.isDefaultReadOnly(), "Default read-only should be true after setting to TRUE.");

            // 3. Set to false
            ds.setDefaultReadOnly(Boolean.FALSE);
            assertFalse(ds.isDefaultReadOnly(), "Default read-only should be false after setting to FALSE.");

            // 4. Set back to true
            ds.setDefaultReadOnly(Boolean.TRUE);
            assertTrue(ds.isDefaultReadOnly(), "Default read-only should be true again after setting back to TRUE.");

            // 5. Reset to null
            ds.setDefaultReadOnly(null);
            assertNull(ds.isDefaultReadOnly(), "Default read-only should be null after being explicitly set to null.");
        }
    }
}

package org.apache.commons.dbcp2.datasources;

import org.junit.jupiter.api.Test;
import static org.junit.jupiter.api.Assertions.assertArrayEquals;
import static org.junit.jupiter.api.Assertions.assertEquals;
import static org.junit.jupiter.api.Assertions.assertNull;

import java.util.Arrays;

/**
 * Tests for {@link UserPassKey}.
 */
public class UserPassKeyTest1 {

    @Test
    public void testClear() {
        // 1. Validate username clear behavior (it should not be cleared)

        // 1a. Null username
        UserPassKey keyNullUser = new UserPassKey(null, "password");
        keyNullUser.clear();
        assertNull(keyNullUser.getUserName(), "Username should remain null after clear");

        // 1b. Empty username
        UserPassKey keyEmptyUser = new UserPassKey("", "password");
        keyEmptyUser.clear();
        assertEquals("", keyEmptyUser.getUserName(), "Username should remain empty after clear");

        // 1c. Non-empty username
        final String nonEmptyUser = "testuser";
        UserPassKey keyNonEmptyUser = new UserPassKey(nonEmptyUser, "password");
        keyNonEmptyUser.clear();
        assertEquals(nonEmptyUser, keyNonEmptyUser.getUserName(), "Username should be unchanged after clear");

        // 2. Validate password (from String) clear behavior

        // 2a. Null username and null password
        UserPassKey keyNullPass = new UserPassKey(null, (String) null);
        keyNullPass.clear();
        assertNull(keyNullPass.getPassword(), "Password should be null after clear when initialized as null");

        // 2b. Empty string credentials
        UserPassKey keyEmptyPass = new UserPassKey("", "");
        keyEmptyPass.clear();
        assertEquals("", keyEmptyPass.getPassword(), "Password should be empty string after clear");

        // 2c. Non-empty credentials
        final String nonEmptyPass = "secret";
        UserPassKey keyNonEmptyPass = new UserPassKey("user", nonEmptyPass);
        keyNonEmptyPass.clear();
        char[] expectedClearedPass = new char[nonEmptyPass.length()];
        Arrays.fill(expectedClearedPass, '\0');
        assertEquals(new String(expectedClearedPass), keyNonEmptyPass.getPassword(),
                "Password should be filled with null characters after clear");

        // 3. Validate password (from char array) clear behavior

        // 3a. Null username and null char array password
        UserPassKey keyNullCharPass = new UserPassKey(null, (char[]) null);
        keyNullCharPass.clear();
        assertNull(keyNullCharPass.getPasswordChars(), "Password char array should be null after clear");

        // 3b. Empty string credentials (as char array)
        UserPassKey keyEmptyCharPass = new UserPassKey("", "".toCharArray());
        keyEmptyCharPass.clear();
        assertArrayEquals("".toCharArray(), keyEmptyCharPass.getPasswordChars(),
                "Password char array should be an empty array after clear");

        // 3c. Non-empty credentials (as char array)
        final char[] nonEmptyCharPass = "anotherSecret".toCharArray();
        UserPassKey keyNonEmptyCharPass = new UserPassKey("user", nonEmptyCharPass);
        keyNonEmptyCharPass.clear();
        char[] expectedClearedCharPass = new char[nonEmptyCharPass.length];
        Arrays.fill(expectedClearedCharPass, '\0');
        assertArrayEquals(expectedClearedCharPass, keyNonEmptyCharPass.getPasswordChars(),
                "Password char array should be filled with null characters after clear");
    }
}

