package org.apache.commons.dbcp2.datasources;

import org.apache.commons.dbcp2.cpdsadapter.DriverAdapterCPDS;
import org.apache.commons.dbcp2.Utils;
import org.junit.jupiter.api.AfterEach;
import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.Test;

import javax.sql.DataSource;
import java.sql.Connection;
import java.time.Duration;
import java.util.Stack;

import static org.junit.jupiter.api.Assertions.assertFalse;
import static org.junit.jupiter.api.Assertions.assertNull;
import static org.junit.jupiter.api.Assertions.assertTrue;

public class PerUserPoolDataSourceTest {

    private static final Duration DURATION_1_MILLISECOND = Duration.ofMillis(1);
    private DataSource ds;
    private String user;

    @BeforeEach
    public void setUp() throws Exception {
        user = "foo";
        final DriverAdapterCPDS pcds = new DriverAdapterCPDS();
        pcds.setDriver("org.apache.commons.dbcp2.TesterDriver");
        pcds.setUrl("jdbc:apache:commons:testdriver");
        pcds.setUser(user);
        pcds.setPassword("bar");
        pcds.setAccessToUnderlyingConnectionAllowed(true);

        final PerUserPoolDataSource tds = new PerUserPoolDataSource();
        tds.setConnectionPoolDataSource(pcds);
        tds.setDefaultMaxTotal(10); // Replaced call to getMaxTotal() with a default value
        tds.setDefaultMaxWait(DURATION_1_MILLISECOND); // Replaced call to getMaxWaitDuration()
        tds.setPerUserMaxTotal(user, 10); // Replaced call to getMaxTotal()
        tds.setPerUserMaxWait(user, DURATION_1_MILLISECOND); // Replaced call to getMaxWaitDuration()
        tds.setDefaultTransactionIsolation(Connection.TRANSACTION_READ_COMMITTED);
        tds.setDefaultAutoCommit(Boolean.TRUE);
        ds = tds;
    }

    @Test
    public void testIsDefaultReadOnly() throws Exception {
        try (final PerUserPoolDataSource ds = new PerUserPoolDataSource()) {
            assertNull(ds.isDefaultReadOnly());
            ds.setDefaultReadOnly(true);
            assertTrue(ds.isDefaultReadOnly().booleanValue());
            ds.setDefaultReadOnly(false);
            assertFalse(ds.isDefaultReadOnly().booleanValue());
            ds.setDefaultReadOnly(true);
            assertTrue(ds.isDefaultReadOnly().booleanValue());
            ds.setDefaultReadOnly(null);
            assertNull(ds.isDefaultReadOnly());
        }
    }

    private void closeConnections(final Stack<Connection> connectionStack) {
        while (!connectionStack.isEmpty()) {
            Utils.closeQuietly((AutoCloseable) connectionStack.pop());
        }
    }

    @AfterEach
    public void tearDown() throws Exception {
        closeConnections(new Stack<>());
        if (ds != null) {
            ((PerUserPoolDataSource) ds).close();
        }
    }
}
