package org.apache.commons.exec;

import static org.junit.jupiter.api.Assertions.assertEquals;
import static org.junit.jupiter.api.Assertions.assertNotNull;

import java.time.Duration;
import java.util.concurrent.ThreadFactory;

import org.junit.jupiter.api.Test;

/**
 * Tests {@link Watchdog.Builder}.
 */
public class WatchdogBuilderTest {

    @Test
    public void testBuild() {
        // Test default construction
        final Watchdog.Builder builder1 = Watchdog.builder();
        final Watchdog watchdog1 = builder1.get();
        assertNotNull(watchdog1, "Default construction should succeed.");

        // Test null timeout
        final Watchdog.Builder builder2 = Watchdog.builder();
        builder2.setTimeout(null);
        final Watchdog watchdog2 = builder2.get();
        assertNotNull(watchdog2, "Construction with null timeout should succeed.");

        // Test specific timeout
        final Watchdog.Builder builder3 = Watchdog.builder();
        final Duration oneMinute = Duration.ofMinutes(1);
        builder3.setTimeout(oneMinute);
        final Watchdog watchdog3 = builder3.get();
        assertEquals(oneMinute, watchdog3.getTimeout(), "Timeout should be one minute.");

        // Test null thread factory resulting in non-null watchdog
        final Watchdog.Builder builder4 = Watchdog.builder();
        builder4.setThreadFactory(null);
        final Watchdog watchdog4 = builder4.get();
        assertNotNull(watchdog4, "Construction with null thread factory should succeed.");

        // Test null thread factory resulting in a default thread factory
        final Watchdog.Builder builder5 = Watchdog.builder();
        builder5.setThreadFactory(null);
        final Watchdog watchdog5 = builder5.get();
        assertNotNull(watchdog5.getThreadFactory(), "Default thread factory should be created.");
    }
}

