package org.apache.commons.exec;

import org.junit.jupiter.api.Test;
import java.time.Duration;
import static org.junit.jupiter.api.Assertions.assertEquals;
import static org.junit.jupiter.api.Assertions.assertNotNull;

/**
 * Test case for the {@code ExecuteWatchdog} builder.
 */
public class ExecuteWatchdogTest1 {

    @Test
    public void testWatchdogBuilder() {
        // 1. Verify that a functional watchdog monitor is created with default settings
        final ExecuteWatchdog watchdogDefault = ExecuteWatchdog.builder().get();
        assertNotNull(watchdogDefault, "Default watchdog should not be null");

        // 2. Verify that a valid watchdog is created even when timeout is unspecified
        final ExecuteWatchdog watchdogUnspecifiedTimeout = ExecuteWatchdog.builder().setTimeout(null).get();
        assertNotNull(watchdogUnspecifiedTimeout, "Watchdog with unspecified timeout should not be null");

        // 3. Verify a watchdog with a one-minute timeout
        final Duration timeout = Duration.ofMinutes(1);
        final ExecuteWatchdog watchdogWithTimeout = ExecuteWatchdog.builder().setTimeout(timeout).get();
        assertNotNull(watchdogWithTimeout, "Watchdog with timeout should not be null");
        assertNotNull(watchdogWithTimeout.getWatchdog(), "Inner watchdog should not be null");
        assertEquals(timeout, watchdogWithTimeout.getWatchdog().getTimeout(), "Timeout should be set to 1 minute");

        // 4. Verify watchdog creation with unconfigured threading
        final ExecuteWatchdog watchdogUnspecifiedThreading = ExecuteWatchdog.builder().setThreadFactory(null).get();
        assertNotNull(watchdogUnspecifiedThreading, "Watchdog with unspecified threading should not be null");

        // 5. Verify the resulting watchdog has a valid threading capability
        assertNotNull(watchdogUnspecifiedThreading.getWatchdog(), "Inner watchdog for unspecified threading should not be null");
        assertNotNull(watchdogUnspecifiedThreading.getWatchdog().getThreadFactory(), "Thread factory for unspecified threading should not be null");
    }
}

