package org.apache.commons.exec;

import static org.assertj.core.api.Assertions.assertThat;

import java.time.Duration;

import org.junit.jupiter.api.Test;

/**
 * Create a test case to validate that the process monitoring system can be properly configured through its
 * configuration interface under various setup scenarios.
 */
public class DefaultExecutorConfigurationTest {

    @Test
    public void testProcessMonitoringConfiguration() {
        // Scenario 1: Basic component without custom settings.
        // Confirms a valid component is created with no active monitoring (watchdog is null).
        final DefaultExecutor executor = new DefaultExecutor();
        assertThat(executor.getWatchdog()).isNull();
        assertThat(executor.getStreamHandler()).isNotNull().isInstanceOf(PumpStreamHandler.class);

        // Scenario 2: Configuration with missing timeout settings.
        // This is implicitly tested above; the system produces a valid component without a watchdog.
        final DefaultExecutor executorWithMissingTimeout = new DefaultExecutor();
        assertThat(executorWithMissingTimeout).isNotNull();
        assertThat(executorWithMissingTimeout.getWatchdog()).isNull();

        // Scenario 3: Configuration with a specific duration limit.
        // Verifies the monitoring component correctly reflects the time constraint.
        final ExecuteWatchdog watchdog = new ExecuteWatchdog(Duration.ofMinutes(1));
        executor.setWatchdog(watchdog);
        assertThat(executor.getWatchdog()).isSameAs(watchdog);

        // Scenario 4: Graceful handling of missing thread management settings.
        // A new executor gets a default PumpStreamHandler.
        final DefaultExecutor executorWithDefaultStreamHandler = new DefaultExecutor();
        assertThat(executorWithDefaultStreamHandler.getStreamHandler()).isNotNull().isInstanceOf(PumpStreamHandler.class);

        // Scenario 5: Combined configuration with a duration limit but without explicit thread management settings.
        // Ensures the default thread management capabilities are maintained.
        final DefaultExecutor combinedExecutor = new DefaultExecutor();
        final ExecuteStreamHandler initialStreamHandler = combinedExecutor.getStreamHandler();
        final ExecuteWatchdog combinedWatchdog = new ExecuteWatchdog(Duration.ofMinutes(1));

        combinedExecutor.setWatchdog(combinedWatchdog);

        assertThat(combinedExecutor.getWatchdog()).isSameAs(combinedWatchdog);
        assertThat(combinedExecutor.getStreamHandler()).isNotNull();
        assertThat(combinedExecutor.getStreamHandler()).isSameAs(initialStreamHandler);
    }
}

