package org.apache.commons.jexl3;

import org.apache.commons.jexl3.introspection.JexlPermissions;
import org.apache.commons.jexl3.introspection.JexlSandbox;
import org.junit.jupiter.api.Test;

import static org.junit.jupiter.api.Assertions.assertEquals;
import static org.junit.jupiter.api.Assertions.assertNotNull;
import static org.junit.jupiter.api.Assertions.assertThrows;

public class PermissionsAndSandboxTest {

    @Test
    public void testPermissionsAndSandboxInteraction() {
        // Instantiate a baseline JEXL engine and verify it can execute a simple script
        JexlEngine jexlEngine = new JexlBuilder().create();
        JexlScript baseScript = jexlEngine.createScript("'hello'.toString()");
        Object result = baseScript.execute(null);
        assertEquals("hello", result, "Baseline engine should execute toString()");

        // Compose a restricted permissions instance that explicitly allows getSimpleName on Class
        JexlPermissions permissions = JexlPermissions.RESTRICTED
                .compose(Class.class.getName() + ".getSimpleName");

        // Instantiate a sandbox configured to disallow reading and writing but allow execution
        // and explicitly permit toString on String
        JexlSandbox sandbox = new JexlSandbox(false, false, true);
        sandbox.allow(Object.class); // allow execution on Object class
        sandbox.permissions(String.class.getName()).execute("toString");

        // Configure a new JEXL engine with safe mode disabled, silent mode disabled,
        // the composed permissions, and the configured sandbox
        JexlEngine restrictedEngine = new JexlBuilder()
                .safe(false)
                .silent(false)
                .permissions(permissions)
                .sandbox(sandbox)
                .create();

        JexlContext context = new MapContext();
        context.set("s", "test-string");
        context.set("o", new Object());

        // Verify the sandbox permits String's toString invocation
        JexlScript toStringScript = restrictedEngine.createScript("s.toString()");
        Object toStringResult = toStringScript.execute(context);
        assertEquals("test-string", toStringResult, "Sandbox should permit String.toString()");

        // Confirm the sandbox blocks getClass method invocation
        JexlScript getClassScript = restrictedEngine.createScript("s.getClass()");
        JexlException.Method methodException = assertThrows(
                JexlException.Method.class,
                () -> getClassScript.execute(context),
                "Sandbox should block getClass() invocation"
        );
        assertNotNull(methodException);

        // Validate the sandbox allows property-style class access
        JexlScript classPropertyScript = restrictedEngine.createScript("s.class");
        Object classPropertyResult = classPropertyScript.execute(context);
        assertEquals(String.class, classPropertyResult, "Sandbox should allow property-style class access");

        // Verify permissions allow getSimpleName
        JexlScript getSimpleNameScript = restrictedEngine.createScript("o.class.simpleName");
        Object simpleNameResult = getSimpleNameScript.execute(context);
        assertEquals("Object", simpleNameResult, "Permissions should allow getSimpleName");

        // Confirm permissions block getClassLoader
        JexlScript getClassLoaderScript = restrictedEngine.createScript("o.class.classLoader");
        JexlException.Property propertyException = assertThrows(
                JexlException.Property.class,
                () -> getClassLoaderScript.execute(context),
                "Permissions should block getClassLoader"
        );
        assertNotNull(propertyException);
    }
}

package org.apache.commons.jexl3;

import org.apache.commons.jexl3.introspection.JexlPermissions;
import org.apache.commons.jexl3.introspection.JexlSandbox;
import org.junit.jupiter.api.Test;

import static org.junit.jupiter.api.Assertions.assertEquals;
import static org.junit.jupiter.api.Assertions.assertNotNull;
import static org.junit.jupiter.api.Assertions.assertThrows;

/**
 * Tests the enforcement of layered access control policies.
 */
public class LayeredAccessControlTest {

    @Test
    public void testLayeredAccessControlPolicies() throws Exception {
        // Establish a baseline evaluation environment with default settings.
        final JexlEngine jexlDefault = new JexlBuilder().create();
        final JexlExpression baseExpr = jexlDefault.createExpression("'foo'.toUpperCase()");
        assertEquals("FOO", baseExpr.evaluate(null), "Baseline evaluation should succeed");

        // Compose a restrictive access policy.
        final JexlPermissions permissions = JexlPermissions.parse(
                "java.lang.*",
                "java.util.*",
                "java.math.*",
                "java.lang.String.toUpperCase",
                "java.lang.Object.class",
                "java.lang.Class.simpleName",
                "java.lang.Class.name"
        );

        // Establish a dual-layer security configuration.
        final JexlSandbox sandbox = new JexlSandbox(false);
        sandbox.block(Object.class).execute("getClass");

        // Configure the evaluation system to operate in strict mode and apply security layers.
        final JexlEngine jexlSecured = new JexlBuilder()
                .strict(true)
                .sandbox(sandbox)
                .permissions(permissions)
                .create();

        final JexlContext context = new MapContext();

        // Verify that the explicitly authorized text conversion operation still succeeds.
        final JexlExpression conversionExpr = jexlSecured.createExpression("'foo'.toUpperCase()");
        assertEquals("FOO", conversionExpr.evaluate(context), "Explicitly allowed method should succeed");

        // Confirm the system properly blocks attempts to retrieve type information through direct inspection methods.
        final JexlExpression directInspectionExpr = jexlSecured.createExpression("'foo'.getClass()");
        assertThrows(JexlException.Method.class, () -> directInspectionExpr.evaluate(context),
                "Direct inspection method 'getClass()' should be blocked by the sandbox");

        // Validate that accessing type characteristics through property-style notation remains permitted.
        final JexlExpression propertyInspectionExpr = jexlSecured.createExpression("'foo'.class");
        final Object classObject = propertyInspectionExpr.evaluate(context);
        assertNotNull(classObject, "Property-style access '.class' should be permitted");
        assertEquals(String.class, classObject, "Property-style access should return the correct Class object");

        // Ensure the system successfully retrieves the simplified type name.
        final JexlExpression simpleNameExpr = jexlSecured.createExpression("'foo'.class.simpleName");
        assertEquals("String", simpleNameExpr.evaluate(context),
                "Access to 'simpleName' property should be allowed by permissions");

        // Verify the system correctly prevents access to restricted type loader information.
        final JexlExpression restrictedPropExpr = jexlSecured.createExpression("'foo'.class.classLoader");
        assertThrows(JexlException.Property.class, () -> restrictedPropExpr.evaluate(context),
                "Access to 'classLoader' property should be denied by permissions");
    }
}

