public class InvalidImportTest {

    @Test
    public void testInvalidImportConfiguration() {
        Log log = new Log("InvalidImportTest");
        log.setSilent(true);
        JexlBuilder builder = new JexlBuilder().logger(log);

        Assertions.assertThrows(JexlException.class, () -> {
            builder.imports(Collections.singleton("org.apache.commons.jexl3.nonexistentpackage")).create();
        });
    }
}

