package org.apache.commons.jexl3;

import org.apache.commons.jexl3.internal.Debugger;
import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.Test;

import java.util.Collections;
import java.util.logging.Level;
import java.util.logging.Logger;

/**
 * Tests for JexlBuilder.
 */
public class JexlBuilderTest {

    /**
     * Verifies that the debugger state is clean.
     */
    private void checkDebugger() {
        Assertions.assertFalse(new Debugger().isDebugging(), "Debugger should not be active");
    }

    @Test
    public void testInvalidPackageImport() {
        final Logger logger = Logger.getLogger(JexlEngine.class.getName());
        final Level level = logger.getLevel();
        logger.setLevel(Level.SEVERE);

        try {
            final JexlBuilder builder = new JexlBuilder();
            final java.util.Collection<String> imports = Collections.singleton("com.invalid.nonexistent.package;");
            builder.imports(imports);

            Assertions.assertThrows(JexlException.class, builder::create, "JEXL engine creation should fail with an invalid import");

        } finally {
            logger.setLevel(level);
            checkDebugger();
        }
    }
}

