package org.apache.commons.jexl3;

import org.apache.commons.jexl3.introspection.JexlPermissions;
import org.junit.jupiter.api.Test;
import org.junit.jupiter.api.Assertions;

import java.util.logging.Level;
import java.util.logging.Logger;

public class PermissionsTest {

    @Test
    public void testImportRestriction() {
        // Suppress logging for this test
        Logger logger = Logger.getLogger(JexlEngine.class.getName());
        Level oldLevel = logger.getLevel();
        logger.setLevel(Level.OFF);

        try {
            // Deny access to the java.net package
            JexlPermissions permissions = new JexlPermissions() {
                @Override
                public boolean importPackage(final String name) {
                    return !"java.net".equals(name);
                }
            };

            JexlEngine jexl = new JexlBuilder().permissions(permissions).create();

            // Attempting to import a class from a restricted package should fail
            String script = "import java.net.URL; new('java.net.URL', 'http://www.apache.org')";

            JexlException.Security thrown = Assertions.assertThrows(JexlException.Security.class, () -> {
                jexl.createScript(script);
            });
            Assertions.assertNotNull(thrown);
            Assertions.assertTrue(thrown.getMessage().contains("import"));

        } finally {
            // Restore logging level
            logger.setLevel(oldLevel);
        }
    }
}

