package org.apache.commons.jexl3;

import org.apache.commons.jexl3.internal.Debugger;
import org.junit.Assert;
import org.junit.Test;

/**
 * Tests for parser rejection of illegal control flow statements in switch expressions.
 */
public class SwitchExpressionFlowControlTest extends JexlTestCase {

    public SwitchExpressionFlowControlTest() {
        super("SwitchExpressionFlowControlTest");
    }

    @Test
    public void testIllegalControlFlowInSwitchExpression() throws Exception {
        // A JEXL engine with safe mode disabled and strict mode enabled is required.
        final JexlEngine jexl = new JexlBuilder().safe(false).strict(true).create();

        // Attempt to use a 'return' statement within a switch expression case.
        final String returnScript = "var x = switch(y) { case 1 -> return z; default -> 42; }";
        // The parser should reject this, so we catch the expected exception.
        final JexlException.Parsing returnException =
                Assert.assertThrows(JexlException.Parsing.class, () -> jexl.createScript(returnScript));
        // Verify the error message indicates 'return' is the issue.
        Assert.assertTrue("The error message should contain the keyword 'return'", returnException.getMessage().contains("return"));

        // Attempt to use a 'break' statement without a value within a switch expression case.
        final String breakScript = "var x = switch(y) { case 1 -> break; default -> 42; }";
        // The parser should also reject this.
        final JexlException.Parsing breakException =
                Assert.assertThrows(JexlException.Parsing.class, () -> jexl.createScript(breakScript));
        // Verify the error message indicates 'break' is the issue.
        Assert.assertTrue("The error message should contain the keyword 'break'", breakException.getMessage().contains("break"));

        // On completion, validate debugger script reconstruction as a teardown activity.
        final Debugger debugger = new Debugger();
        final JexlScript validScript = jexl.createScript("var x = switch(y) { case 1 -> 10; default -> 20; }");
        Assert.assertTrue("Debugger should successfully reconstruct the script", debugger.debug(validScript));
        Assert.assertTrue("Reconstructed script should contain 'switch'", debugger.toString().contains("switch"));
    }
}

