package org.apache.commons.jexl3;

import org.junit.jupiter.api.Test;
import org.junit.jupiter.api.Assertions;

public class SwitchExpressionFlowControlTest {

    @Test
    public void testSwitchExpressionDisallowsFlowControl() {
        // Configure engine to be strict and not safe
        JexlEngine jexl = new JexlBuilder().safe(false).strict(true).create();

        // Test case 1: switch expression with a return statement
        String returnScript = "var x = switch (y) { case 1: 1; case 2: return 2; default: 3; };";
        JexlException.Parsing returnException = Assertions.assertThrows(
            JexlException.Parsing.class,
            () -> jexl.createScript(returnScript),
            "Should fail parsing due to 'return' in switch expression"
        );
        Assertions.assertTrue(
            returnException.getMessage().contains("return"),
            "Error message should mention the prohibited 'return' keyword"
        );

        // Test case 2: switch expression with a break statement without value
        String breakScript = "var x = switch (y) { case 1: 1; case 2: break; default: 3; };";
        JexlException.Parsing breakException = Assertions.assertThrows(
            JexlException.Parsing.class,
            () -> jexl.createScript(breakScript),
            "Should fail parsing due to 'break' in switch expression"
        );
        Assertions.assertTrue(
            breakException.getMessage().contains("break"),
            "Error message should mention the prohibited 'break' keyword"
        );
    }
}

package org.apache.commons.jexl3;

import java.util.Collection;
import java.util.Collections;
import java.util.logging.Level;
import java.util.logging.Logger;
import org.apache.commons.jexl3.internal.Debugger;
import org.junit.After;
import org.junit.Assert;
import org.junit.Test;

/**
 * Tests for JEXL builder permission handling.
 * This class inherits from JexlTestCase to access the shared JEXL engine instance
 * for debugger state validation in the teardown method.
 */
public class JexlBuilderPermissionsTest extends JexlTestCase {

    /**
     * Trivial constructor.
     */
    public JexlBuilderPermissionsTest() {
        super("JexlBuilderPermissionsTest");
    }

    /**
     * Restores the default logging level and performs debugger state validation
     * by delegating to the inherited checkDebugger method.
     * @throws Exception if any error occurs during teardown.
     */
    @After
    public void tearDown() throws Exception {
        // Restore logger level
        Logger.getLogger(JexlEngine.class.getName()).setLevel(Level.INFO);
        // Delegate to the inherited helper method for debugger validation
        checkDebugger(JEXL);
    }

    /**
     * Validates that the JexlBuilder correctly enforces permissions and denies
     * the creation of an engine when configured with a restricted import.
     */
    @Test
    public void testBuilderImportRestriction() {
        // Suppress all but severe log messages from JEXL Engine for this test
        Logger.getLogger(JexlEngine.class.getName()).setLevel(Level.SEVERE);

        // Define a collection with a restricted package name
        final Collection<String> imports = Collections.singleton("java.lang.ProcessBuilder");

        // Instantiate a JexlBuilder and configure it with the restricted import
        final JexlBuilder builder = new JexlBuilder().imports(imports);

        try {
            // Attempt to create the engine, which should be blocked
            builder.create();
            Assert.fail("Engine creation should have failed due to a permission violation.");
        } catch (final JexlException.Permission e) {
            // Assert that the correct, JEXL-specific exception was thrown
            Assert.assertTrue("Exception message should indicate an import permission issue.",
                              e.getMessage().contains("import"));
        }
    }
}

