package org.apache.commons.jexl3;

import org.apache.commons.jexl3.JexlBuilder;
import org.apache.commons.jexl3.JexlContext;
import org.apache.commons.jexl3.JexlEngine;
import org.apache.commons.jexl3.JexlScript;
import org.apache.commons.jexl3.MapContext;
import org.apache.commons.jexl3.internal.Debugger;
import org.junit.Assert;
import org.junit.Test;

/**
 * Tests for switch statement fall-through behavior.
 */
public class SwitchFallThroughTest {

    /**
     * Helper method to perform debugger validation on the engine's cache.
     * <p>
     * This method recreates each expression from the engine's internal cache
     * and compares them for equality with their originals to ensure the
     * engine's parsing and caching mechanisms maintain script integrity.
     * </p>
     *
     * @param jexl the JEXL engine instance to validate.
     */
    private void checkDebugger(final JexlEngine jexl) {
        final Debugger debugger = new Debugger();
        Assert.assertTrue("JEXL cache recreated expressions should be equal to their originals", debugger.assertRecreate(jexl));
    }

    @Test
