public class SwitchExpressionTest {

    private void checkDebug(JexlEngine engine, JexlScript script) {
        JexlNode ast = script.getAst();
        assertNotNull(ast);

        Debugger debugger = new Debugger();
        String deconstructed1 = debugger.deconstruct(ast);
        assertNotNull(deconstructed1);

        String reconstructedSource = debugger.reconstruct(deconstructed1);
        assertNotNull(reconstructedSource);

        JexlScript script2 = engine.createScript(reconstructedSource);
        assertNotNull(script2);

        JexlNode ast2 = script2.getAst();
        assertNotNull(ast2);

        String deconstructed2 = debugger.deconstruct(ast2);
        assertEquals(deconstructed1, deconstructed2);
    }

    @Test
    public void testSwitchExpression() throws Exception {
        JexlEngine jexl = new JexlBuilder().safe(false).strict(true).create();
        String scriptText = "var x = switch (i) { case 1 -> 2; case 2 -> 3; default -> 4; }; return x;";
        JexlScript script = jexl.createScript(scriptText, "i");

        JexlContext context = new MapContext();
        Object result = script.execute(context, 1);

        assertEquals(2, result);

        checkDebug(jexl, script);
    }
}

