/**
 * Validates that the JEXL template engine correctly processes strings with special characters
 * and performs variable substitution in multi-line templates.
 */
public class TemplateCharacterAndSubstitutionTest {

    /**
     * Creates a test case to verify template evaluation, ensuring that special characters like
     * newlines and escaped backticks are handled correctly, and that variables are dynamically
     * substituted within a multi-line formatted script.
     */
    @Test
    public void testTemplateProcessingWithSpecialCharactersAndVariableSubstitution() {
        // Configure a JEXL engine with default settings
        JexlEngine jexlEngine = new JexlBuilder().create();
        JxltEngine jxltEngine = jexlEngine.createJxltEngine();

        // Define a template string with a newline and an escaped backtick delimiter
        String templateWithEscapes = "`Line one\nLine two with a \` backtick`";
        JxltEngine.Template template1 = jxltEngine.createTemplate(templateWithEscapes);
        StringWriter writer1 = new StringWriter();
        template1.evaluate(null, writer1);

        // Verify the output matches the expected formatted text with the backtick properly escaped
        String expectedFormattedText = "Line one\nLine two with a ` backtick";
        assertThat(writer1.toString()).isEqualTo(expectedFormattedText);

        // Establish an evaluation context and register a variable
        JexlContext context = new MapContext();
        context.set("greeting", "Hello from JEXL");

        // Prepare a multi-line script that interpolates the variable multiple times
        String multiLineScript = "`${greeting}\n${greeting}`";
        JxltEngine.Template template2 = jxltEngine.createTemplate(multiLineScript);
        StringWriter writer2 = new StringWriter();
        template2.evaluate(context, writer2);

        // Confirm the output combines the variable value with the specified formatting
        String expectedSubstitutedText = "Hello from JEXL\nHello from JEXL";
        assertThat(writer2.toString()).isEqualTo(expectedSubstitutedText);
    }
}

package org.apache.commons.jexl3.introspection;

import org.apache.commons.jexl3.JexlBuilder;
